																						)}
																					>
																						<Button
																							className="btn-circle btn-warning mr-2"
																							onClick={() => {
																								this.setState({
																									photoMode: true,
																									lampiran: prop.attributes.attachFile.url()
																								});
																							}}
																						>
																							<i className="fa fa-eye" />
																						</Button>
																					</OverlayTrigger>
																				)}
																			<OverlayTrigger
																				placement="right"
																				overlay={tooltip(
																					'Lampiran'
																				)}
																			>
																				<Button
																					className="btn-circle btn-warning mr-2"
																					onClick={() => {
																						this.setState({
																							editMode: true,
																							userId: prop.id,
																							userIndex: key,
																							fullnames: prop.get(
																								'fullname'
																							)
																						});
																					}}
																				>
																					<i className="fa fa-check" />
																				</Button>
																			</OverlayTrigger>
																			<OverlayTrigger
																				placement="right"
																				overlay={tooltip('Reject')}
																			>
																				<Button
																					className="btn-circle btn-danger"
																					onClick={(e) => {
																						this.setState({
																							deleteMode: true,
																							userId: prop.id,
																							userIndex: key,
																							fullnames: prop.get(
																								'fullname'
																							)
																						});
																					}}
																				>
																					<i className="fa fa-close" />
																				</Button>
																			</OverlayTrigger>
																		</td>
																	</tr>
																))}
															</tbody>
														</Table>
													</Col>
													// <Col md={3}>
