import React, { Component } from 'react';
import axios from 'axios';
import { Form, Col, Button, Spinner, Alert } from 'react-bootstrap';
import { login } from 'utils';
import { useHistory } from 'react-router';
import '../assets/css/my.css';
import Parse from 'parse';

class Login extends Component {
	constructor(props) {
		super(props);
		this.state = {
			hello: '',
			username: '',
			password: '',
			loading: false,
			error: null
		};

		this.handleLogin = this.handleLogin.bind(this);
	}

	componentDidMount() {}

	handleLogin(e) {
		e.preventDefault();
		const { username, password } = this.state;
		Parse.User
			.logIn(username, password)
			.then((x) => {
				const currentUser = Parse.User.current();
				var game = new Parse.Query('Izin');
				const Quiz = Parse.Object.extend('Izin');
				const QuizCategory = Parse.Object.extend('Leader');
				const leader = new QuizCategory();
				const query = new Parse.Query(Quiz);

				const categoryId = req.body.categoryId;
				leader.id = currentUser.get('leaderId').id;

				query.equalTo('leaderId', leader);
				// game.equalTo('leaderId', currentUser.get('leaderId').id);
				console.log(query);
				console.log(currentUser.get('leaderId').id);
				return currentUser;
			})
			.catch((err) => console.log(err));
	}

	// getHelloWorld() {
	// 	const url = 'http://35.247.147.177:3001/hello';
	// 	axios.get(url).then(({ data }) => {
	// 		this.setState({ hello: data });
	// 	});
	// }
	render() {
		const { hello, loading, error } = this.state;

		return (
			<div className="containerz">
				<div className="login">
					<p style={{ textAlign: 'center', fontWeight: 'bolder' }}>
						{error ? error : 'Login First'}
					</p>
					<Form onSubmit={this.handleLogin}>
						<Form.Group controlId="formBasicEmail">
							<Form.Label>Email address</Form.Label>
							<Form.Control
								type="text"
								placeholder="Enter email"
								onChange={(e) => {
									console.log(this.state.username);
									this.setState({ username: e.target.value });
								}}
							/>
							<Form.Text className="text-muted">
								We'll never share your email with anyone else.
							</Form.Text>
						</Form.Group>

						<Form.Group controlId="formBasicPassword">
							<Form.Label>Password</Form.Label>
							<Form.Control
								type="password"
								placeholder="Password"
								onChange={(e) => {
									console.log(this.state.password);
									this.setState({ password: e.target.value });
								}}
							/>
						</Form.Group>
						<Button variant="primary" type="submit" disabled={loading ? true : false}>
							{loading ? (
								<Spinner
									as="span"
									animation="grow"
									size="sm"
									role="status"
									aria-hidden="true"
								/>
							) : (
								'Login'
							)}
						</Button>
					</Form>
				</div>
			</div>
		);
	}
}

export default Login;
