import React, { Component } from 'react';
import { Form, Col, Row } from 'react-bootstrap';

class Quiz extends Component {
	constructor(props) {
		super(props);
		this.state = {
			questionCounter: 0,
			answer: '',
			score: 0,
			myQuestion: [
				{
					question: 'Who invented JavaScript?',
					answers: [ 'Douglas Crockford', 'Sheryl Sandberg', 'Brendan Eich' ],
					correctAnswer: 2
				},
				{
					question: 'Which one of these is a JavaScript package manager?',
					answers: [ 'Node.js', 'TypeScript', 'npm' ],
					correctAnswer: 2
				},
				{
					question: 'Which tool can you use to ensure code quality?',
					answers: [ 'Angular', 'jQuery', 'RequireJS' ],
					correctAnswer: 2
				}
			]
		};
		this.onSelect = this.onSelect.bind(this);
	}
	componentDidMount() {
		// if (this.state.questionCounter >= 2) {
		// 	this.setState({ questionCounter: 0 });
		// }
	}
	onSelect(e) {
		e.preventDefault();
		this.setState({ answer: e.target.label });
		console.log(this.state.answer);
	}
	shuffle(array) {
		var currentIndex = array.length,
			temporaryValue,
			randomIndex;

		// While there remain elements to shuffle...
		while (0 !== currentIndex) {
			// Pick a remaining element...
			randomIndex = Math.floor(Math.random() * currentIndex);
			currentIndex -= 1;

			// And swap it with the current element.
			temporaryValue = array[currentIndex];
			array[currentIndex] = array[randomIndex];
			array[randomIndex] = temporaryValue;
		}

		return array;
	}
	render() {
		const { myQuestion } = this.state;
		return (
			<div>
				<Form.Group as={Row}>
					<Form.Label as="legend" column sm={2}>
						Radios
					</Form.Label>
					<Col sm={10}>
						{myQuestion.map((x, i) => (
							<QuestionBox
								question={x.question}
								option={x.answers}
								correctAnswer={x.correctAnswer}
							/>
						))}
					</Col>
				</Form.Group>
				<button
					onClick={() => {
						if (this.state.answer === '') {
							return alert('must be answered');
						}
						if (
							this.state.answer ===
							this.state.myQuestion[this.state.questionCounter].correctAnswer
						) {
							this.setState((prevState) => ({
								score: prevState.score + 10
							}));
							console.log(this.state.score);
						}
						if (this.state.questionCounter == 2) {
							console.log(this.state.questionCounter);
							return this.setState({ questionCounter: 0 });
						}
						this.setState((prevState) => ({
							questionCounter: prevState.questionCounter + 1
						}));
						console.log(this.state.questionCounter);
					}}
				>
					Next
				</button>
			</div>
			// <Form>
			// 	{myQuestions.map(({ answers, question }, index) => {
			// 		return Object.keys(answers).map((key, i) => {
			// 			return (
			// 				<div key={i} className="mb-3">
			// 					<Form.Group>
			// 						<Col sm={10}>
			// 							<Form.Check
			// 								onClick={(e) => console.log(e.target.value)}
			// 								type="radio"
			// 								value={key}
			// 								id={i}
			// 								label={answers[key]}
			// 							/>
			// 						</Col>
			// 					</Form.Group>
			// 				</div>
			// 			);
			// 		});
			// 		// Object.keys(answers).forEach((key, i) => {
			// 		// 	<div className="mb-3">
			// 		// 		<Form.Check
			// 		// 			onClick={(e) => console.log(e.target.value)}
			// 		// 			type="radio"
			// 		// 			value={key}
			// 		// 			id={`default-radio`}
			// 		// 			label={answers[key]}
			// 		// 		/>
			// 		// 	</div>;
			// 		// });
			// 	})}
			// </Form>
		);
	}
}

function QuestionBox({ question, option, correctAnswer }) {
	return (
		<div>
			<p>{question}</p>
			{option.map((text, index) => {
				<button>{text}</button>;
			})}
		</div>
	);
}

export default Quiz;
