/*!

=========================================================
* Light Bootstrap Dashboard React - v1.3.0
=========================================================

* Product Page: https://www.creative-tim.com/product/light-bootstrap-dashboard-react
* Copyright 2019 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/light-bootstrap-dashboard-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React, { Component } from 'react';
import { Container, Row, Col, FormGroup, ControlLabel, FormControl, Form } from 'react-bootstrap';

import { Card } from 'components/Card/Card.jsx';
import { FormInputs } from 'components/FormInputs/FormInputs.jsx';
import { UserCard } from 'components/UserCard/UserCard.jsx';
import Button from 'components/CustomButton/CustomButton.jsx';

import avatar from 'assets/img/faces/face-3.jpg';
import Axios from 'axios';
import { baseurl } from 'utils/baseurl';

class UserProfile extends Component {
	constructor(props) {
		super(props);
		this.state = {
			trainee: [],
			error: '',
			batch: 1,
			loading: false,
			addMode: false,
			editMode: false,
			deleteMode: false
		};

		this.handleSearch = this.handleSearch.bind(this);
	}

	componentDidMount() {
		this.getTrainee();
	}

	getTrainee() {
		this.setState({ loading: true });
		const data = {
			batch: this.state.batch
		};
		Axios.post(baseurl('trainee/batch'), data)
			.then((x) => {
				this.setState({ trainee: x.data, loading: false });
			})
			.catch((err) => this.setState({ error: err, loading: false }));
	}

	handleAdd() {
		e.preventDefault();
		const {
			contentImage,
			contentBody,
			contentTitle,
			contentType,
			batch,
			schedulex
		} = this.state;

		const user = new Parse.User();
		user.set('username', 'A string');
		user.set('email', 'A string');
		user.set(
			'profilePicture',
			new Parse.File('resume.txt', { base64: btoa('My file content') })
		);
		user.set('dateOfBirth', 'A string');
		user.set('placeOfBirth', 'A string');
		user.set('phoneNumber', 'A string');
		user.set('role', 'A string');
		user.set('fullname', 'A string');
		user.set('batch', 1);
		user.set('status', 1);
		user.set('password', '#Password123');
		console.log('sched', schedulex);
		user
			.save()
			.then((x) => {
				console.log('success', {
					objectId: x.id,
					contentBody: x.get('contentBody'),
					contentFile: x.get('contentFile').url(),
					contentTitle: x.get('contentTitle'),
					schedule: x.get('schedule'),
					updatedAt: x.get('updatedAt')
				});
				console.log('data', x);
				this.setState({
					modal: false,
					batch: []
				});
				window.location.reload(false);
			})
			.catch((err) => {
				console.log(err);
			});
	}

	handleEdit() {}

	handleUpdate() {}

	handleDelete() {}

	handleSearch(e) {
		e.preventDefault();

		this.setState({ loading: true });

		const data = {
			batch: this.state.batch
		};

		Axios.post(baseurl('trainee/batch'), data)
			.then((x) => {
				console.log(x.data);
				this.setState({ trainee: x.data, loading: false });
			})
			.catch((err) => this.setState({ error: err, loading: false }));
	}

	render() {
		const { trainee, error, loading, batch } = this.state;

		return (
			<div className="content">
				<Container fluid>
					{/* <Row> */}
					{/* <Col md={12}> */}
					{/* <Card
								title={<h4>{`Batch ${batch}`}</h4>}
								content={ */}
					<div>
						<Form onSubmit={this.handleSearch} style={{ marginBottom: '20px' }}>
							<Form.Group as={Row} controlId="formHorizontalEmail">
								{/* <Form.Label column sm={2}>
									Email
								</Form.Label> */}
								<Col sm={2}>
									<Button variant="primary" type="submit">
										<i className="fa fa-plus" /> Add trainee
									</Button>
								</Col>
								<Col sm={{ span: 4, offset: 1 }} className="pull-right">
									<Form.Control
										as="select"
										defaultValue={1}
										onChange={(e) => {
											this.setState({ batch: e.target.value });
										}}
									>
										{[ 1, 2, 3 ].map((x) => (
											<option value={x}>{`Batch ${x}`}</option>
										))}
									</Form.Control>
								</Col>
								<Col sm={{ span: 4, offset: 1 }}>
									{/* <button
										type="button"
										className="btn btn-labeled btn-primary"
										onClick={() => {
											this.setState({ modal: true });
										}}
									> */}
									{/* <span className="btn-label">
											<i className="fa fa-search" />
										</span>{' '}
										Search
									</button> */}
									<Button variant="primary" type="submit">
										<i className="fa fa-search" />{' '}
										{loading ? 'Fetching...' : 'Search'}
									</Button>
								</Col>
							</Form.Group>
						</Form>
					</div>
					<Row>
						{/* {loading && <Col md={12}>Fetching data...</Col>} */}
						{trainee.length < 1 ? (
							<Col md={12}>No data found...</Col>
						) : (
							trainee.map((x) => (
								<Col md={3}>
									<UserCard
										out={x.status}
										// bgImage="https://ununsplash.imgix.net/photo-1431578500526-4d9613015464?fit=crop&fm=jpg&h=300&q=75&w=400"
										bgImage={
											<img
												src="https://ununsplash.imgix.net/photo-1431578500526-4d9613015464?fit=crop&fm=jpg&h=300&q=75&w=400"
												alt="..."
											/>
										}
										avatar={x.profile.url}
										name={x.fullname}
										userName={x.username}
										description={
											<span>
												"Lamborghini Mercy
												<br />
												Your chick she so thirsty
												<br />
												I'm in that two seat Lambo"
											</span>
										}
										socials={
											<div>
												<Button simple>
													<i className="fa fa-facebook-square" />
												</Button>
												<Button simple>
													<i className="fa fa-twitter" />
												</Button>
												<Button simple>
													<i className="fa fa-google-plus-square" />
												</Button>
											</div>
										}
									/>
								</Col>
							))
						)}
					</Row>

					{/* </Col> */}
					{/* </Row> */}
				</Container>
			</div>
		);
	}
}

export default UserProfile;
