import React, { Component } from 'react';
import {
	Container,
	Row,
	Col,
	FormGroup,
	ControlLabel,
	FormControl,
	Form,
	Tooltip,
	OverlayTrigger,
	Table
} from 'react-bootstrap';

import { Card } from 'components/Card/Card.jsx';
import { FormInputs } from 'components/FormInputs/FormInputs.jsx';
import { UserCard } from 'components/UserCard/UserCard.jsx';
import Button from 'components/CustomButton/CustomButton.jsx';

import avatar from 'assets/img/faces/face-3.jpg';
import Axios from 'axios';
import { baseurl } from 'utils/baseurl';
import Parse, { User } from 'parse';
import ModalHandler from './Category/ModalHandler';
import moment from 'moment';
import DateTime from 'react-datetime';
import { Link } from 'react-router-dom';
import { handleConvert } from 'utils/converter';
import { slicename } from 'utils/slicename';

class EarlyLeave extends Component {
	constructor(props) {
		super(props);
		this.state = {
			trainee: [],
			leave: [],
			user: [],
			data: [],
			leaveArray: [],
			error: '',
			userId: '',
			fullnames: '',
			imageSelfies: '',
			times: '',
			reasons: '',
			userIndex: 0,
			status: 3,
			statusCalendar: 0,
			loading: false,
			addMode: false,
			editMode: false,
			deleteMode: false,
			buttonLoading: false
		};

		this.handleSearch = this.handleSearch.bind(this);
		this.handleDelete = this.handleDelete.bind(this);
		this.handleApprove = this.handleApprove.bind(this);
		this.handleReject = this.handleReject.bind(this);
		this.handleFilter = this.handleFilter.bind(this);

	}

	componentDidMount() {
		this.getTrainee();
		this.getUser();
	}

	getTrainee() {
		this.setState({ loading: true });
		const Leave = Parse.Object.extend('EarlyLeave');
		const Leader = Parse.Object.extend('Leader');
		const leader = new Leader();
		const query = new Parse.Query(Leave);

		const d = new Date();
		const start = new moment(d);
		start.startOf('day');
		const finish = new moment(start);
		finish.add(1, 'day');

		console.log(Parse.User.current().get('leaderId').id);

		leader.id = Parse.User.current().get('leaderId').id;
		query.equalTo('leaderId', leader);
		query.equalTo('status', 3);
		query.greaterThanOrEqualTo('createdAt', start.toDate());
		query.lessThan('createdAt', finish.toDate());
		query.find().then((x) => {
			console.log(x);
			this.setState({ leave: x, loading: false });
		});
	}

	// get user
	getUser() {
		this.setState({ loading: true });
		const User = Parse.Object.extend('User');
		const Leader = Parse.Object.extend('Leader');
		const leader = new Leader();
		const query = new Parse.Query(User);

		console.log(Parse.User.current().get('leaderId').id);

		leader.id = Parse.User.current().get('leaderId').id;
		query.equalTo('leaderId', leader);
		query.equalTo('roles', 'staff');
		query.find().then((x) => {
			console.log(x);
			this.setState({ user: x, loading: false });
		});
	}

	handleFilter(e) {
		e.preventDefault();
		this.setState({ loading: true });
		console.log(this.state.status);

		const leave = Parse.Object.extend('EarlyLeave');
		const Leader = Parse.Object.extend('Leader');
		const leader = new Leader();
		const query = new Parse.Query(leave);

		const d = new Date();
		const start = new moment(d);
		start.startOf('day');
		const finish = new moment(start);
		finish.add(1, 'day');

		console.log(Parse.User.current().get('leaderId').id);

		leader.id = Parse.User.current().get('leaderId').id;
		query.equalTo('leaderId', leader);
		query.equalTo('status', parseInt(this.state.status));
		// query.greaterThanOrEqualTo('createdAt', start.toDate());
		// query.lessThan('createdAt', finish.toDate());
		query.find().then((x) => {
			console.log(x);
			this.setState({ leave: x, loading: false });
		});
	}

	handleApprove(e) {
		this.setState({ loading: true });
		const leave = Parse.Object.extend('EarlyLeave');
		const query = new Parse.Query(leave);

		query.get(this.state.userId).then((x) => {
			x.set('status', 1);
			x.save().then(() => {
				const newOvertime = [...this.state.leave];
				newOvertime.splice(this.state.userIndex, 1);
				this.setState({
					leave: newOvertime,
					editMode: false,
					loading: false
				});
			});
		});
	}

	handleApproveAll(e) {
		this.setState({ loading: true });
		const leave = Parse.Object.extend('EarlyLeave');
		const query = new Parse.Query(leave);

		query.get(e).then((x) => {
			x.set('status', 1);
			x.save().then(() => {
				const newOvertime = [...this.state.leave];
				newOvertime.splice(this.state.userIndex, 1);
				this.setState({
					leave: newOvertime,
					editMode: false,
					loading: false
				});
			});
		});
	}

	handleReject(e) {
		this.setState({ loading: true });
		const leave = Parse.Object.extend('EarlyLeave');
		const query = new Parse.Query(leave);

		query.get(this.state.userId).then((x) => {
			x.set('status', 0);
			x.save().then(() => {
				const newOvertime = [...this.state.leave];
				newOvertime.splice(this.state.userIndex, 1);
				this.setState({
					leave: newOvertime,
					deleteMode: false,
					loading: false
				});
			});
		});
	}

	handleRejectAll(e) {
		this.setState({ loading: true });
		const leave = Parse.Object.extend('EarlyLeave');
		const query = new Parse.Query(leave);

		query.get(e).then((x) => {
			x.set('status', 0);
			x.save().then(() => {
				const newOvertime = [...this.state.leave];
				newOvertime.splice(this.state.userIndex, 1);
				this.setState({
					leave: newOvertime,
					deleteMode: false,
					loading: false
				});
			});
		});
	}

	handleEdit(id) {
		const User = new Parse.User();
		const query = new Parse.Query(User);

		// Finds the user by its ID
		query
			.get(id)
			.then((user) => {
				// Updates the data we want
				this.setState({
					traineeId: id,
					editMode: true,
					username: user.get('username'),
					email: user.get('email'),
					dob: user.get('dateOfBirth'),
					pob: user.get('placeOfBirth'),
					phoneNumber: user.get('phoneNumber'),
					fullname: user.get('fullname'),
					batch: user.get('batch')
				});
			})
			.catch((err) => console.log(err));
	}

	handleUpdate(e) {
		e.preventDefault();

		this.setState({ loading: true });

		const {
			username,
			email,
			password,
			passwordConf,
			dob,
			pob,
			phoneNumber,
			fullname,
			batch,
			profile
		} = this.state;

		const User = new Parse.User();
		const query = new Parse.Query(User);

		query.get(this.state.traineeId).then((user) => {
			user.set('username', `${username}-batch${batch}`);
			user.set('email', email);
			if (profile !== '') user.set('profile', new Parse.File('profile.jpg', profile));
			user.set('dateOfBirth', dob);
			user.set('placeOfBirth', pob);
			user.set('phoneNumber', phoneNumber);
			user.set('role', 'trainee');
			user.set('fullname', fullname);
			user.set('batch', parseInt(batch));
			user.set('status', 1);
			user.set('password', password);
			user.set('out', false);
			user.save().then((z) => {
				this.setState({ loading: false });
				window.location.reload(false);
			});
		});
	}

	handleDelete() {
		this.setState({ loading: true });
		const User = new Parse.User();
		const query = new Parse.Query(User);

		query.get(this.state.traineeId).then((x) => {
			x.set('status', 0);
			x.save().then((z) => {
				this.setState({ late: z, loading: false });
				window.location.reload(false);
			});
		});
	}

	handleSearch(e) {
		e.preventDefault();

		console.log(this.state.batch);

		this.setState({ loading: true });

		const data = {
			batch: this.state.batch
		};

		Axios.post(baseurl('trainee/batch'), data)
			.then((x) => {
				console.log(x.data);
				this.setState({ trainee: x.data, loading: false });
			})
			.catch((err) => this.setState({ error: err, loading: false }));
	}


	render() {
		const { leave, error, loading, batch, startDate, endDate } = this.state;

		// handleApproveAll(e) {
		// 	this.setState({ loading: true });
		// 	const leaveAll = Parse.Object.extend('EarlyLeave');
		// 	const query = new Parse.Query(leaveAll);

		// 	query.get(this.state.userId).then((x) => {
		// 		x.set('status', 1);
		// 		x.save().then(() => {
		// 			const newOvertime = [...this.state.leaveAll];
		// 			newOvertime.splice(this.state.userIndex, 1);
		// 			this.setState({
		// 				leaveAll: newOvertime,
		// 				approveAllMode: false,
		// 				loading: false
		// 			});
		// 		});
		// 	});
		// }

		const ApproveAllIds = () => {

			let arrayIds = [];
			leave.forEach(d => {
				if (d.select) {
					arrayIds.push(d.id);
					// arrayIds.push(d.get('fullname'));
				}
			});
			// return arrayIds;
			// console.log(arrayIds);

			const leaveAll = Parse.Object.extend('EarlyLeave');
			const query = new Parse.Query(leaveAll);
			// console.log(query.get(arrayIds));
			let totalData = 0;

			// arrayIds.map((id) => {
			// console.log(id);
			// for (let i = 0; i < arrayIds.length; i++) {

			arrayIds.map((id) => {
				this.handleApproveAll(id);
				// console.log(id);
				// query.get(id).then((x) => {

				// 	x.set('status', 1);
				// 	x.save(id).then(() => {
				// 		console.log('success');
				// 		totalData++;
				// 		if (totalData == arrayIds.length) {
				// 			console.log('stop');
				// 			id = '';
				// 		}
				// 		// const newOvertime = [...this.state.leaveAll];
				// 		// newOvertime.splice(this.state.userIndex, 1);
				// 		// this.setState({
				// 		// 	leaveAll: newOvertime,
				// 		// 	approveAllMode: false,
				// 		// 	loading: false
				// 		// });
				// 	});
				// });
			});
			// }
			// });
		}

		const RejectAllIds = () => {
			let arrayIds = [];
			leave.forEach(d => {
				if (d.select) {
					arrayIds.push(d.id);
					// arrayIds.push(d.get('fullname'));
				}
			});

			arrayIds.map((id) => {
				this.handleRejectAll(id);
			});
		}

		// const { user, error, loading } = this.state;
		// const id = this.props.match.params.id;
		const {
			username,
			email,
			password,
			passwordConf,
			dob,
			pob,
			phoneNumber,
			fullname,
			profile
		} = this.state;
		const tooltip = (msg) => <Tooltip id="button-tooltip">{msg}</Tooltip>;

		return (
			<div className="content">
				<ModalHandler
					show={this.state.deleteMode}
					title="Reject confirmation"
					handleHide={() => this.setState({ deleteMode: false })}
					handleSave={this.handleReject}
					loading={this.state.loading}
					body={'Reject request ' + this.state.fullnames + ' ?'}
				/>
				<ModalHandler
					show={this.state.editMode}
					title="Approve confirmation"
					handleSave={this.handleApprove}
					loading={this.state.loading}
					handleHide={() => this.setState({ editMode: false })}
					body={'Approve request ' + this.state.fullnames + ' ?'}
				/>
				<ModalHandler
					show={this.state.approveAllMode}
					title="List Approve"
					loading={this.state.loading}
					handleHide={() => this.setState({ approveAllMode: false })}
					body={'Sure Approve All Request?'}
				/>
				<ModalHandler
					show={this.state.detailMode}
					title="Foto Absen"
					loading={this.state.loading}
					handleHide={() => this.setState({ detailMode: false })}
					body={<img width="100%" height={300} src={this.state.imageSelfies} />}
				/>
				<Container fluid>
					<Row>
						<Col md={12}>
							<Card
								title="Early Request"
								ctTableFullWidth
								ctTableResponsive
								content={
									<div>
										<Row>
											<Col>
												<Form
													onSubmit={this.handleFilter}
													style={{ marginBottom: '20px' }}
												>
													<Form.Group
														as={Row}
														controlId="formHorizontalEmail"
													>
														<Col sm={1}>
															<p>Search by</p>
														</Col>
														<Col
															sm={{ span: 2 }}
															className="pull-right"
														>
															<Form.Control
																as="select"
																// defaultValue={1}
																onChange={(e) => {
																	console.log(e.target.value);
																	this.setState({
																		status: e.target.value
																	});
																}}
															>
																{[3, 1, 0].map((x) => (
																	<option value={x}>
																		{handleConvert(x)}
																	</option>
																))}
															</Form.Control>
														</Col>
														<Col
															sm={{ span: 2 }}
															className="pull-right"
														>
															<Form.Control
																as="select"
																// defaultValue={1}
																onChange={(e) => {
																	console.log(e.target.value);
																	this.setState({
																		statusCalendar: e.target.value
																	});
																}}
															>
																<option value="">Pilih Kategori</option>
																{[4, 5, 6].map((x) => (
																	<option value={x}>
																		{handleConvert(x)}
																	</option>
																))}
															</Form.Control>
														</Col>
														<Col sm={{ span: 2 }} className="pull-right">
															<Form.Control
																type="date"
																value={startDate}
																onChange={(e) => {
																	console.log(e.target.value);
																	this.setState({
																		startDate: e.target.value
																	});
																}}
																required='true'
															/>
														</Col>
														<Col sm={{ span: 2 }} className="pull-right">
															<Form.Control
																type="date"
																value={endDate}
																onChange={(e) => {
																	this.setState({ endDate: e.target.value })
																}}
																required
															/>
														</Col>
														<Col sm={{ span: 2 }}>
															<Button
																variant="primary"
																type="submit"
																disable={loading ? 'true' : 'false'}
															>
																<i className="fa fa-search" />{' '}
																{loading ? 'Fetching...' : 'Search'}
															</Button>
														</Col>
													</Form.Group>
													<Col sm={{ span: 0 }} className="float-none">
														<Button className="m-1" onClick={() => {
															// this.setState({
															// 	approveAllMode: true,
