																	<span>
																		<span>
																			<strong>
																				Absen masuk:
																		</strong>
																			<br />
																			{moment(
																				x.get('time')
																			).format(
																				'DD/MM/YYYY [at] HH:mm:ss'
																			)}
																		</span>
																		<br />
																		<span>
																			<strong>
																				Late reason:
																		</strong>
																			<br />
																			{x.get('alasan')}
																		</span>
																	</span>
																}
																status={x.get('status')}
																socials={
																	<div>
																		<OverlayTrigger
																			placement="right"
																			overlay={tooltip('Approve')}
																		>
																			<Button
																				simple
																				onClick={() => {
																					this.setState({
																						editMode: true,
																						userId: x.id,
																						userIndex: i,
																						fullnames: x.get(
																							'fullname'
																						)
																					});
																				}}
																			>
																				<i className="fa fa-check" />
																			</Button>
																		</OverlayTrigger>
																		<OverlayTrigger
																			placement="right"
																			overlay={tooltip('Reject')}
																		>
																			<Button
																				simple
																				onClick={(e) => {
																					this.setState({
																						deleteMode: true,
																						userId: x.id,
																						userIndex: i,
																						fullnames: x.get(
																							'fullname'
																						)
																					});
																				}}
																			>
																				<i className="fa fa-close" />
																			</Button>
																		</OverlayTrigger>
																	</div>
																}
															/>
														</Col>
													))
												)} */}
										</Row>
									</div>
								}
							/>
						</Col>
					</Row>

					{/* </Col> */}
					{/* </Row> */}
				</Container>
			</div>
		);
	}
}

export default EarlyLeave;
