import React, { Component } from 'react';
import { Button, Container, Row, Col } from 'react-bootstrap';
import Card from 'components/Card/Card';
import ChartistGraph from 'react-chartist';
import Axios from 'axios';
import moment from 'moment';
import { Link } from 'react-router-dom';

class MasterCategory extends Component {
	constructor(props) {
		super(props);
		this.state = {
			category: [],
			loading: false
		};
	}

	componentDidMount() {
		this.getCategory();
	}

	getCategory() {
		this.setState({ loading: true });

		const url = 'http://localhost:5000/api/category/list';

		return Axios.get(url)
			.then((response) => {
				this.setState({ category: response.data, loading: false });
			})
			.catch((err) => console.log(err));
	}

	render() {
		const { category, loading } = this.state;

		return (
			<div className="content">
				<Container fluid>
					<Row>
						<Col md={12}>
							<Card
								category={
									<Link to="/quiz/add">
										<button
											type="button"
											className="btn btn-labeled btn-primary"
											onClick={() => {}}
										>
											<span className="btn-label">
												<i className="glyphicon glyphicon-plus" />
											</span>{' '}
											Add Category
										</button>
									</Link>
								}
								content={
									<Row>
										{loading && <Col md={12}>Loading, please wait...</Col>}
										{category.map((x) => (
											<Col md={3}>
												<Card
													title={x.category}
													category={
														'Jadwal : ' +
														moment(x.schedule).format('DD/MM/YYYY')
													}
													stats={moment(x.createdAt).fromNow()}
													statsIcon="pe-7s-clock"
													content={
														<div id="chartActivity" className="">
															{x.description}
														</div>
													}
													legend={
														<div className="legend">
															<Link to="/quiz/add">
																<button
																	type="button"
																	className="btn btn-circle btn-default"
																	onClick={() => {}}
																>
																	<span className="btn-label">
																		<i className="glyphicon glyphicon-eye-open" />
																	</span>
																	{''}
																	{/* View Detail */}
																</button>
															</Link>
															{'     '}
															<Link to="/quiz/add">
																<button
																	type="button"
																	className="btn btn-circle btn-default"
																	style={{ marginLeft: '5px' }}
																	onClick={() => {}}
																>
																	<span className="btn-label">
																		<i className="glyphicon glyphicon-eye-open" />
																	</span>{' '}
																	{/* View Detail */}
																</button>
															</Link>
														</div>
													}
												/>
											</Col>
										))}
									</Row>
								}
							/>
						</Col>
					</Row>
				</Container>
			</div>
		);
	}
}

export default MasterCategory;
