import React, { Component } from 'react';
import {
    Container,
    Row,
    Col,
    FormGroup,
    ControlLabel,
    FormControl,
    Form,
    Tooltip,
    OverlayTrigger,
    Table
} from 'react-bootstrap';

import { Card } from 'components/Card/Card.jsx';
import { FormInputs } from 'components/FormInputs/FormInputs.jsx';
import { UserCard } from 'components/UserCard/UserCard.jsx';
import Button from 'components/CustomButton/CustomButton.jsx';

import avatar from 'assets/img/faces/face-3.jpg';
import Axios from 'axios';
import { baseurl } from 'utils/baseurl';
import Parse from 'parse';
import ModalHandler from './Category/ModalHandler';
import moment from 'moment';
import DateTime from 'react-datetime';
import { Link } from 'react-router-dom';
import { handleConvert } from 'utils/converter';

class Cuti extends Component {
    constructor(props) {
        super(props);
        this.state = {
            trainee: [],
            izin: [],
            error: '',
            userId: '',
            status: 3,
            userIndex: 0,
            fullnames: '',
            loading: false,
            addMode: false,
            editMode: false,
            deleteMode: false,
            buttonLoading: false,
            checkAll: false,
            checkId: [],
            checkOne: false,
            lampiran: '',
            photoMode: false
        };

        this.handleApprove = this.handleApprove.bind(this);
        this.handleReject = this.handleReject.bind(this);
        this.handleFilter = this.handleFilter.bind(this);
        this.handleAllCheck = this.handleAllCheck.bind(this);
        this.handleChildCheck = this.handleChildCheck.bind(this);
    }

    componentDidMount() {
        this.getTrainee();
    }

    handleAllCheck(e) {
        let izin = this.state.izin;
        let collecId = [];

        izin.map((x) => {
            x.isChecked = e.target.checked;
            if (x.isChecked) {
                collecId.push(x.id);
            } else {
                collecId = [];
            }

            return x;
        });

        this.setState({ izin: izin, checkId: collecId }, () => console.log(this.state.checkId));
    }

    handleChildCheck(e) {
        let { izin } = this.state;
        const { checkId } = this.state;
        let checked = e.target.value;
        izin.map((x) => {
            console.log('bandingkan', x.id === e.target.value);
            if (x.id === e.target.value) {
                console.log('sama');
                x.isChecked = e.target.checked;
                if (x.isChecked) {
                    this.setState(
                        (prevState) => ({
                            checkId: [...this.state.checkId, checked]
                        }),
                        () => console.log(this.state.checkId)
                    );
                } else {
                    const index = checkId.indexOf(checked);
                    if (index > -1) {
                        checkId.splice(index, 1);
                        this.setState(
                            (prevState) => ({
                                checkId: checkId
                            }),
                            () => console.log(this.state.checkId)
                        );
                    }
                }
            }
        });

        this.setState({ izin: izin });
    }

    approveChecked = (e) => {
        const { checkId } = this.state;
        const checkIdLength = checkId.length;
        const Izin = Parse.Object.extend('Izin');
        const query = new Parse.Query(Izin);
        let totalData = 0;

        checkId.map((id) => {
            console.log(id);
            //query.equalTo('objectId', id);
            query.get(id).then((x) => {
                // x.set('status', 1);
                // x.save().then((x) => {
                //  console.log('success');
                // });
                console.log(x);
            });
            // query
            //  .get(id)
            //  .then((x) => {
            //      x.set('status', 1);
            //      x
            //          .save()
            //          .then((x) => {
            //              console.log('success');
            //              totalData++;
