* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React, { Component } from 'react';
import axios from 'axios';
import { Container, Row, Col, Table } from 'react-bootstrap';

import Card from 'components/Card/Card.jsx';
import { thArray, tdArray } from 'variables/Variables.jsx';

class MasterQuiz extends Component {
	constructor(props) {
		super(props);
		this.state = {
			reco: [],
			loading: false
		};
	}

	componentDidMount() {
		this.getData();
	}

	baseUrl(route) {
		return `http://localhost:5000/api/${route}`;
	}

	getData() {
		return axios.get(this.baseUrl('quiz-list')).then((response) => {
			console.log(response.data);
			this.setState({ reco: response.data });
		});
	}

	render() {
		const { reco, loading } = this.state;

		return (
			<div className="content">
				<Container fluid>
					<Row>
						<Col md={12}>
							<Card
								category={
									<button
										type="button"
										class="btn btn-labeled btn-primary"
										onClick={() => {
											this.setState({ loading: true });
										}}
									>
										<span class="btn-label">
											<i class="glyphicon glyphicon-plus" />
										</span>{' '}
										{loading ? 'Loading...' : 'Add Question'}
									</button>
								}
								ctTableFullWidth
								ctTableResponsive
								content={
									<Table striped hover>
										<thead>
											<tr>
												<th>NO</th>
												<th>CATEGORY</th>
												<th>QUESTION</th>
												<th>SCHEDULE</th>
												<th>START TIME</th>
												<th>END TIME</th>
												<th>ACTION</th>
											</tr>
										</thead>
										<tbody key={1}>
											{reco.map((prop, key) => (
												<tr key={key}>
													<td>{key + 1}</td>
													<td>{prop.categoryId.category}</td>
													<td>{prop.question}</td>
													<td>{prop.schedule.iso}</td>
													<td>{prop.startTime.iso}</td>
													<td>{prop.endTime.iso}</td>
												</tr>
											))}
										</tbody>
									</Table>
								}
							/>
						</Col>
					</Row>
				</Container>
			</div>
		);
	}
}

export default MasterQuiz;
