import React, { Component } from 'react';
import { Container, Row, Col, OverlayTrigger, Tooltip, Button, Modal, Form } from 'react-bootstrap';
import Card from 'components/Card/Card';
import ChartistGraph from 'react-chartist';
import Axios from 'axios';
import moment from 'moment';
import { Link } from 'react-router-dom';

class MasterCategory extends Component {
	constructor(props) {
		super(props);
		this.state = {
			category: [],
			loading: false,
			modal: false
		};
	}

	componentDidMount() {
		this.getCategory();
	}

	getCategory() {
		this.setState({ loading: true });

		const url = 'http://localhost:5000/api/category/list';

		return Axios.get(url)
			.then((response) => {
				this.setState({ category: response.data, loading: false });
			})
			.catch((err) => console.log(err));
	}

	render() {
		const { category, loading, modal } = this.state;
		const remove = <Tooltip id="remove_tooltip">Remove</Tooltip>;

		return (
			<div className="content">
				<Modal show={modal} onHide={() => this.setState({ modal: false })}>
					<Modal.Header closeButton>
						<Modal.Title
							style={{
								marginTop: '0px',
								marginBottom: '0px',
								color: 'Black',
								fontWeight: 'bold'
							}}
						>
							Add Quiz Category
						</Modal.Title>
					</Modal.Header>
					<Modal.Body>
						<Form>
							<Form.Group controlId="formCategory">
								<Form.Label>Quiz category</Form.Label>
								<Form.Control type="text" placeholder="Enter category" />
								{/* <Form.Text className="text-muted">
									We'll never share your email with anyone else.
								</Form.Text> */}
							</Form.Group>

							<Form.Group controlId="formDescription">
								<Form.Label>Description</Form.Label>
								<Form.Control as="textarea" placeholder="Enter description" />
							</Form.Group>
							<Form.Group controlId="formBasicCheckbox">
								<Form.Check inline type="checkbox" value={1} label="Batch 1" />
								<Form.Check inline type="checkbox" value={2} label="Batch 2" />
								<Form.Check inline type="checkbox" value={3} label="Batch 3" />
							</Form.Group>
							<Button variant="primary" type="submit">
								Submit
							</Button>
						</Form>
					</Modal.Body>
					<Modal.Footer>
						<Button variant="secondary" onClick={() => this.setState({ modal: false })}>
							Close
						</Button>
						<Button variant="primary" onClick={() => this.setState({ modal: false })}>
							Save Changes
						</Button>
					</Modal.Footer>
				</Modal>
				<Container fluid>
					<Row>
						<Col md={12}>
							<Card
								category={
									<button
										type="button"
										className="btn btn-labeled btn-primary"
										onClick={() => {
											this.setState({ modal: true });
										}}
									>
										<span className="btn-label">
											<i className="fa fa-plus" />
										</span>{' '}
										Add Category
									</button>
								}
								content={
									<Row>
										{loading && <Col md={12}>Loading, please wait...</Col>}
										{category.map((x) => (
											<Col md={3}>
												<Card
													title={x.category}
													category={
														'Jadwal : ' +
														moment(x.schedule).format('DD/MM/YYYY')
													}
													stats={moment(x.createdAt).fromNow()}
													statsIcon="pe-7s-clock"
													content={
														<div id="chartActivity" className="">
															{x.description}
														</div>
													}
													legend={
														<div className="legend">
															<Link
																to={`/admin/list/${x.objectId}/category`}
															>
																<button
																	type="button"
																	className="btn btn-circle btn-primary"
																	onClick={() => {}}
																	style={{ marginRight: '5px' }}
																>
																	<span className="btn-label">
																		<i className="fa fa-eye" />
																	</span>
																</button>
															</Link>
															<Link to="/quiz/add">
																<button
																	type="button"
																	className="btn btn-circle btn-danger"
																	onClick={() => {}}
																>
																	<span className="btn-label">
																		<i className="fa fa-pencil" />
																	</span>
																</button>
															</Link>
															<Link to="/quiz/add">
																<button
																	type="button"
																	className="btn btn-circle btn-warning"
																	style={{ marginLeft: '5px' }}
																	onClick={() => {}}
																>
																	<span className="btn-label">
																		<i className="fa fa-trash" />
																	</span>
																</button>
															</Link>
														</div>
													}
												/>
											</Col>
										))}
									</Row>
								}
							/>
						</Col>
					</Row>
				</Container>
			</div>
		);
	}
}

export default MasterCategory;
