import React, { Component } from 'react';
import { Button, Container, Row, Col } from 'react-bootstrap';
import Card from 'components/Card/Card';
import ChartistGraph from 'react-chartist';
import Axios from 'axios';

class MasterCategory extends Component {
	constructor(props) {
		super(props);
		this.state = {};
	}

	componentDidMount() {
		this.getCategory();
	}

	getCategory() {
		const url = 'http://localhost:5000/api/category/list';

		return Axios.get(url).then((response) => console.log(response.data));
	}

	render() {
		return (
			<div className="content">
				<Container fluid>
					<Row>
						{[ 1, 2, 3, 4, 5, 6 ].map((x) => (
							<Col md={3}>
								<Card
									title="2014 Sales"
									category="All products including Taxes"
									stats="Data information certified"
									statsIcon="fa fa-check"
									content={
										<div id="chartActivity" className="">
											Hello
										</div>
									}
									legend={
										<div className="legend">
											<i className="fa fa-circle text-info" /> Tesla Model S
											<i className="fa fa-circle text-danger" /> BMW 5 Series
										</div>
									}
								/>
							</Col>
						))}
					</Row>
				</Container>
			</div>
		);
	}
}

export default MasterCategory;
