* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React, { Component } from 'react';
import {
	Container,
	Row,
	Col,
	FormGroup,
	ControlLabel,
	FormControl,
	Form,
	Tooltip,
	OverlayTrigger
} from 'react-bootstrap';

import { Card } from 'components/Card/Card.jsx';
import { FormInputs } from 'components/FormInputs/FormInputs.jsx';
import { UserCard } from 'components/UserCard/UserCard.jsx';
import Button from 'components/CustomButton/CustomButton.jsx';

import avatar from 'assets/img/faces/face-3.jpg';
import Axios from 'axios';
import { baseurl } from 'utils/baseurl';
import Parse from 'parse';
import ModalHandler from './Category/ModalHandler';
import moment from 'moment';
import DateTime from 'react-datetime';
import { Link } from 'react-router-dom';

class UserProfile extends Component {
	constructor(props) {
		super(props);
		this.state = {
			trainee: [],
			error: '',
			batch: 1,
			username: '',
			email: '',
			dob: moment(),
			pob: '',
			phoneNumber: '',
			fullname: '',
			password: '',
			passwordConf: '',
			profile: '',
			error: '',
			fullnames: '',
			traineeId: '',
			loading: false,
			addMode: false,
			editMode: false,
			deleteMode: false,
			buttonLoading: false
		};

		this.handleSearch = this.handleSearch.bind(this);
		this.handleAdd = this.handleAdd.bind(this);
		this.handleDelete = this.handleDelete.bind(this);
	}

	componentDidMount() {
		this.getTrainee();
	}

	getTrainee() {
		this.setState({ loading: true });
		const data = {
			batch: 1
		};
		Axios.post(baseurl('trainee/batch'), data)
			.then((x) => {
				this.setState({ trainee: x.data, loading: false });
			})
			.catch((err) => this.setState({ error: err, loading: false }));
	}

	handleAdd(e) {
		e.preventDefault();
		this.setState({ loading: true });
		const {
			username,
			email,
			password,
			passwordConf,
			dob,
			pob,
			phoneNumber,
			fullname,
			batch,
			profile
		} = this.state;

		const user = new Parse.User();
		user.set('username', `${username}-batch${batch}`);
		user.set('email', email);
		if (profile !== '') user.set('profile', new Parse.File('profile.jpg', profile));
		user.set('dateOfBirth', dob);
		user.set('placeOfBirth', pob);
		user.set('phoneNumber', phoneNumber);
		user.set('role', 'trainee');
		user.set('fullname', fullname);
		user.set('batch', parseInt(batch));
		user.set('status', 1);
		user.set('password', password);
		user.set('out', false);
		user
			.signUp()
			.then((x) => {
				console.log('data', x);
				this.setState({
					addMode: false,
					loading: false
				});
				window.location.reload(false);
			})
			.catch((err) => {
				console.log(err);
				this.setState({ loading: false });
			});
	}

	handleEdit(id) {
		const {
			username,
			email,
			password,
			passwordConf,
			dob,
			pob,
			phoneNumber,
			fullname,
			batch,
			profile
		} = this.state;
		const User = new Parse.User();
		const query = new Parse.Query(User);

		// Finds the user by its ID
		query
			.get(id)
			.then((user) => {
				// Updates the data we want
				this.setState({
					traineeId: id,
					editMode: true,
					username: user.get('username'),
					email: user.get('email'),
					dob: user.get('dateOfBirth'),
					pob: user.get('placeOfBirth'),
					phoneNumber: user.get('phoneNumber'),
					fullname: user.get('fullname'),
					batch: user.get('batch')
				});
			})
			.catch((err) => console.log(err));
	}

	handleUpdate() {}

	handleDelete() {
		this.setState({ loading: true });
		const User = new Parse.User();
		const query = new Parse.Query(User);

		query.get(this.state.traineeId).then((x) => {
			x.set('status', 0);
			x.save().then((z) => {
				this.setState({ loading: false });
				window.location.reload(false);
			});
		});
	}

	handleSearch(e) {
		e.preventDefault();

		console.log(this.state.batch);

		this.setState({ loading: true });

		const data = {
			batch: this.state.batch
		};

		Axios.post(baseurl('trainee/batch'), data)
			.then((x) => {
				console.log(x.data);
				this.setState({ trainee: x.data, loading: false });
			})
			.catch((err) => this.setState({ error: err, loading: false }));
	}

	render() {
		const { trainee, error, loading, batch } = this.state;
		const tooltip = (msg) => <Tooltip id="button-tooltip">{msg}</Tooltip>;

		return (
			<div className="content">
				<ModalHandler
					show={this.state.deleteMode}
					title="Delete confirmation"
					handleHide={() => this.setState({ deleteMode: false })}
					handleSave={this.handleDelete}
					body={'Delete trainee ' + this.state.fullnames + ' ?'}
				/>
				<ModalHandler
					show={this.state.editMode}
					title="Edit content"
					handleHide={this.handleHide}
					body={
						<Form onSubmit={this.handleAdd}>
							<Form.Group controlId="formCategory">
								<Form.Label>Fullname</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									type="text"
									placeholder="Enter fullname"
									onChange={(e) => this.setState({ fullname: e.target.value })}
								/>
							</Form.Group>

							<Form.Group controlId="formCategory">
								<Form.Label>Username</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									type="text"
									placeholder="Enter fullname"
									onChange={(e) => this.setState({ username: e.target.value })}
								/>
							</Form.Group>

							<Form.Group controlId="formSubtitle">
								<Form.Label>Email</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									type="email"
									placeholder="Enter email"
									onChange={(e) => this.setState({ email: e.target.value })}
								/>
							</Form.Group>

							<Form.Group controlId="formPasw">
								<Form.Label>Password</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									type="text"
									placeholder="Enter password"
									onChange={(e) => this.setState({ password: e.target.value })}
								/>
							</Form.Group>

							<Form.Group>
								<Form.File
									id="exampleFormControlFile1"
									label="Trainee picture"
									onChange={(e) => {
										this.setState({ profile: e.target.files[0] });
										console.log(e.target.files[0]);
									}}
								/>
							</Form.Group>

							<Form.Group controlId="formCategory">
								<Form.Label>Tempat lahir</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									type="text"
									placeholder="Tempat lahir"
									onChange={(e) => this.setState({ pob: e.target.value })}
								/>
							</Form.Group>

							<Form.Group>
								<Form.Label>Tanggal lahir</Form.Label>
								<DateTime
									timeFormat={false}
									value={this.state.dob}
									onChange={(momentz) => {
										console.log(momentz.toDate());
										this.setState({
											dob: momentz.format('DD/MM/YYYY')
										});
									}}
									inputProps={{
										placeholder: 'Select date',
										readOnly: true
									}}
								/>
							</Form.Group>

							<Form.Group controlId="formGridState">
								<Form.Label>Batch</Form.Label>
								<Form.Control
									as="select"
									onChange={(e) => {
										this.setState({ batch: e.target.value });
										console.log(e.target.value);
									}}
								>
									{[ 1, 2, 3 ].map((x) => (
										<option value={x}>{`Batch ${x}`}</option>
									))}
								</Form.Control>
							</Form.Group>

							<Form.Group controlId="formPhone">
								<Form.Label>Phone number</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									type="number"
									//value={1}
									placeholder="Nomor telfon"
									onChange={(e) => this.setState({ phoneNumber: e.target.value })}
								/>
							</Form.Group>

							<Button variant="primary" type="submit">
								{this.state.loading ? 'Processing...' : 'Submit'}
							</Button>
						</Form>
					}
				/>
				<ModalHandler
					show={this.state.addMode}
					title="Add trainee"
					handleHide={() => this.setState({ addMode: false })}
					body={
						<Form onSubmit={this.handleAdd}>
							<Form.Group controlId="formCategory">
								<Form.Label>Fullname</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									type="text"
									placeholder="Enter fullname"
									onChange={(e) => this.setState({ fullname: e.target.value })}
								/>
							</Form.Group>

							<Form.Group controlId="formCategory">
								<Form.Label>Username</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									type="text"
									placeholder="Enter fullname"
									onChange={(e) => this.setState({ username: e.target.value })}
								/>
							</Form.Group>

							<Form.Group controlId="formSubtitle">
								<Form.Label>Email</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									type="email"
									placeholder="Enter email"
									onChange={(e) => this.setState({ email: e.target.value })}
								/>
							</Form.Group>

							<Form.Group controlId="formPasw">
								<Form.Label>Password</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									type="text"
									placeholder="Enter password"
									onChange={(e) => this.setState({ password: e.target.value })}
								/>
							</Form.Group>

							<Form.Group>
								<Form.File
									id="exampleFormControlFile1"
									label="Trainee picture"
									onChange={(e) => {
										this.setState({ profile: e.target.files[0] });
										console.log(e.target.files[0]);
									}}
								/>
							</Form.Group>

							<Form.Group controlId="formCategory">
								<Form.Label>Tempat lahir</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									type="text"
									placeholder="Tempat lahir"
									onChange={(e) => this.setState({ pob: e.target.value })}
								/>
							</Form.Group>

							<Form.Group>
								<Form.Label>Tanggal lahir</Form.Label>
								<DateTime
									timeFormat={false}
									value={this.state.dob}
									onChange={(momentz) => {
										console.log(momentz.toDate());
										this.setState({
											dob: momentz.format('DD/MM/YYYY')
										});
									}}
									inputProps={{
										placeholder: 'Select date',
										readOnly: true
									}}
								/>
							</Form.Group>

							<Form.Group controlId="formGridState">
								<Form.Label>Batch</Form.Label>
								<Form.Control
									as="select"
									onChange={(e) => {
										this.setState({ batch: e.target.value });
										console.log(e.target.value);
									}}
								>
									{[ 1, 2, 3 ].map((x) => (
										<option value={x}>{`Batch ${x}`}</option>
									))}
								</Form.Control>
							</Form.Group>

							<Form.Group controlId="formPhone">
								<Form.Label>Phone number</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									type="number"
									//value={1}
									placeholder="Nomor telfon"
									onChange={(e) => this.setState({ phoneNumber: e.target.value })}
								/>
							</Form.Group>

							<Button variant="primary" type="submit">
								{this.state.loading ? 'Processing...' : 'Submit'}
							</Button>
						</Form>
					}
				/>
				<Container fluid>
					<Row>
						<Col md={12}>
							<Card
								content={
									<div>
										<div>
											<Form
												onSubmit={this.handleSearch}
												style={{ marginBottom: '20px' }}
											>
												<Form.Group
													as={Row}
													controlId="formHorizontalEmail"
												>
													<Col sm={2}>
														<Button
															variant="primary"
															style={{ margin: '0px' }}
															onClick={() =>
																this.setState({ addMode: true })}
														>
															<i className="fa fa-plus" /> Add trainee
														</Button>
													</Col>
													<Col sm={{ span: 2 }} className="pull-right">
														<Form.Control
															as="select"
															// defaultValue={1}
															onChange={(e) => {
																console.log(e.target.value);
																this.setState({
																	batch: e.target.value
																});
															}}
														>
															{[ 1, 2, 3 ].map((x) => (
																<option
																	value={x}
																>{`Batch ${x}`}</option>
															))}
														</Form.Control>
													</Col>
													<Col sm={{ span: 2 }}>
														<Button
															variant="primary"
															type="submit"
															disable={loading ? 'true' : 'false'}
														>
															<i className="fa fa-search" />{' '}
															{loading ? 'Fetching...' : 'Search'}
														</Button>
													</Col>
												</Form.Group>
											</Form>
										</div>
										<Row>
											{trainee.length < 1 ? (
												<Col md={12}>No data found...</Col>
											) : (
												trainee.map((x, i) => (
													<Col md={3}>
														<UserCard
															out={x.status}
															bgImage={
																<img
