/*!

=========================================================
* Light Bootstrap Dashboard React - v1.3.0
=========================================================

* Product Page: https://www.creative-tim.com/product/light-bootstrap-dashboard-react
* Copyright 2019 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/light-bootstrap-dashboard-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React, { Component } from 'react';
import axios from 'axios';
import {
	Container,
	Row,
	Col,
	Table,
	Tooltip,
	Button,
	OverlayTrigger,
	Modal
} from 'react-bootstrap';

import Card from 'components/Card/Card.jsx';
import { thArray, tdArray } from 'variables/Variables.jsx';
import { Link } from 'react-router-dom';
import moment from 'moment';
import e from 'cors';

class QuizList extends Component {
	constructor(props) {
		super(props);
		this.state = {
			reco: [],
			question: '',
			correctAnswers: 0,
			answers: [],
			trainee: [],
			loading: false,
			addModal: false
		};
		this.handleAddQuestion = this.handleAddQuestion.bind(this);
	}

	componentDidMount() {
		this.getData();
	}

	handleAddQuestion(a, b) {}

	baseUrl(route) {
		return `http://localhost:5000/api/${route}`;
	}

	convertAnswer(answer) {
		switch (answer) {
			case 0:
				return 'A';
				break;
			case 1:
				return 'B';
				break;
			case 2:
				return 'C';
				break;
			case 3:
				return 'D';
				break;
		}
	}

	getData() {
		const data = {
			categoryId: this.props.match.params.id
		};
		return axios.post(this.baseUrl('quiz/category'), data).then((response) => {
			console.log(response.data);
			this.setState({ reco: response.data });
		});
	}

	render() {
		const FORMAT = 'DD/MM/YYYY';
		const FORMAT2 = 'H:mm:ss';
		const { reco, loading, trainee, addModal, question, correctAnswers, answers } = this.state;
		console.log(reco.length);
		const tooltip = <Tooltip id="button-tooltip">Simple tooltip</Tooltip>;

		return (
			<div className="content">
				<ModalHandler
					show={this.state.addModal}
					title="Add quiz"
					handleHide={() => this.setState({ addModal: false })}
					body={
						<Form onSubmit={this.handleSubmit}>
							<Form.Group controlId="formCategory">
								<Form.Label>Pertanyaan</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									type="text"
									value={question}
									placeholder="Enter question"
									onChange={(e) => {
										this.setState({ question: e.target.value });
									}}
								/>
							</Form.Group>

							<Form.Group controlId="formSubtitle">
								<Form.Label>Subtitle</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									type="text"
									value={subtitle}
									placeholder="Enter subtitle"
									onChange={this.handleSubtitle}
								/>
							</Form.Group>

							<Form.Group controlId="formDescription">
								<Form.Label>Description</Form.Label>
								<Form.Control
									as="textarea"
									autoCapitalize="true"
									autoComplete="false"
									value={desc}
									placeholder="Enter description"
									rows={5}
									onChange={this.handleDesc}
								/>
							</Form.Group>

							<Form.Group>
								<Form.Label>Schedule</Form.Label>
								<DateTime
									value={this.state.schedule}
									onChange={(momentz) => {
										this.setState({
											schedule: momentz.format('DD/MM/YYYY [at] hh:mm:ss')
										});
										this.setState({
											schedulex: moment(
												this.state.schedule,
												moment.defaultFormat
											)
										});
									}}
									inputProps={{
										placeholder: 'Select schedule',
										readOnly: true
									}}
								/>
							</Form.Group>

							<Form.Group>
								<Form.Label>Waktu pengerjaan (menit)</Form.Label>
								<Form.Control
									autoComplete="false"
									type="number"
									value={timeInMinutes}
									placeholder="Waktu pengerjaan"
									onChange={this.handleTime}
								/>
							</Form.Group>

							<Form.Group controlId="formBasicCheckbox">
								<Form.Check
									inline
									type="checkbox"
									checked={batch.includes(1) ? 'true' : 'false'}
									value={1}
									label="Batch 1"
									onChange={this.handleDaySelect}
								/>
								<Form.Check
									inline
									type="checkbox"
									checked={batch.includes(2) ? 'true' : 'false'}
									value={2}
									label="Batch 2"
									onChange={this.handleDaySelect}
								/>
								<Form.Check
									inline
									type="checkbox"
									checked={batch.includes(3) ? 'true' : 'false'}
									value={3}
									label="Batch 3"
									onChange={this.handleDaySelect}
								/>
							</Form.Group>
							<Button variant="primary" type="submit">
								Submit
							</Button>
						</Form>
					}
				/>
				<Modal show={modal} onHide={() => this.setState({ modal: false })}>
					<Modal.Header closeButton>
						<Modal.Title>Modal heading</Modal.Title>
					</Modal.Header>
					<Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
					<Modal.Footer>
						<Button variant="secondary" onClick={() => this.setState({ modal: false })}>
							Close
						</Button>
						<Button variant="primary" onClick={() => this.setState({ modal: false })}>
							Save Changes
						</Button>
					</Modal.Footer>
				</Modal>
				<Container fluid>
					<Row>
						<Col md={12}>
							<Card
								category={
									<button
										type="button"
										className="btn btn-labeled btn-primary"
										onClick={() => {
											console.log('clicked');
											this.setState({ addModal: true });
										}}
									>
										<span className="btn-label">
											<i className="glyphicon glyphicon-plus" />
										</span>{' '}
										Add Quiz
									</button>
								}
								ctTableFullWidth
								ctTableResponsive
								content={
									<Table striped hover>
										<thead>
											<tr>
												<th>NO</th>
												<th>CATEGORY</th>
												<th>QUESTION</th>
												<th>OPTION</th>
												<th>JAWABAN</th>
												<th>ACTION</th>
											</tr>
										</thead>
										<tbody key={1}>
											{reco.map((prop, key) => (
												<tr key={key}>
													<td>{key + 1}.</td>
													<td>{prop.categoryId.category}</td>
													<td>{prop.question}</td>
													<td>
														<ol type="A" style={{ paddingLeft: '0' }}>
															{prop.answers.map((sult, i) => (
																<li>{sult}</li>
															))}
														</ol>
													</td>
													<td>
														{this.convertAnswer(prop.correctAnswers)}
													</td>
													<td>
														<button
															type="button"
															className="btn btn-warning btn-circle"
															onClick={() => {
																this.handleEdit(prop.objectId);
															}}
														>
															<i className="fa fa-pencil" />
														</button>
														<button
															type="button"
															className="btn btn-danger btn-circle"
															onClick={() => {
																this.setState({});
															}}
														>
															<i className="fa fa-trash" />
														</button>
													</td>
												</tr>
											))}
										</tbody>
									</Table>
								}
							/>
						</Col>
					</Row>
				</Container>
			</div>
		);
	}
}

export default QuizList;
