/*!

=========================================================
* Light Bootstrap Dashboard React - v1.3.0
=========================================================

* Product Page: https://www.creative-tim.com/product/light-bootstrap-dashboard-react
* Copyright 2019 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/light-bootstrap-dashboard-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React, { Component } from 'react';
import { Container, Row, Col, FormGroup, ControlLabel, FormControl, Form } from 'react-bootstrap';

import { Card } from 'components/Card/Card.jsx';
import { FormInputs } from 'components/FormInputs/FormInputs.jsx';
import { UserCard } from 'components/UserCard/UserCard.jsx';
import Button from 'components/CustomButton/CustomButton.jsx';

import avatar from 'assets/img/faces/face-3.jpg';
import Axios from 'axios';
import { baseurl } from 'utils/baseurl';
import Parse from 'parse';
import ModalHandler from './Category/ModalHandler';
import DateTime from 'react-datetime';

class UserProfile extends Component {
	constructor(props) {
		super(props);
		this.state = {
			trainee: [],
			error: '',
			batch: 1,
			username: '',
			email: '',
			dob: '',
			pob: '',
			phoneNumber: '',
			fullname: '',
			password: '',
			passwordConf: '',
			profile: '',
			error: '',
			loading: false,
			addMode: false,
			editMode: false,
			deleteMode: false
		};

		this.handleSearch = this.handleSearch.bind(this);
		this.handleAdd = this.handleAdd.bind(this);
	}

	componentDidMount() {
		this.getTrainee();
	}

	getTrainee() {
		this.setState({ loading: true });
		const data = {
			batch: 1
		};
		Axios.post(baseurl('trainee/batch'), data)
			.then((x) => {
				this.setState({ trainee: x.data, loading: false });
			})
			.catch((err) => this.setState({ error: err, loading: false }));
	}

	handleAdd(e) {
		e.preventDefault();
		const {
			username,
			email,
			password,
			passwordConf,
			dob,
			pob,
			phoneNumber,
			fullname,
			batch,
			profile
		} = this.state;

		if (password !== passwordConf) {
			return this.setState({ error: "Password didn't match" });
		}

		const user = new Parse.User();
		user.set('username', username);
		user.set('email', email);
		if (profile !== '') user.set('profilePicture', new Parse.File('profile.jpg', profile));
		user.set('dateOfBirth', dob);
		user.set('placeOfBirth', pob);
		user.set('phoneNumber', phoneNumber);
		user.set('role', 'trainee');
		user.set('fullname', fullname);
		user.set('batch', batch);
		user.set('status', 1);
		user.set('password', password);
		user
			.save()
			.then((x) => {
				console.log('success', {
					objectId: x.id,
					contentBody: x.get('contentBody'),
					contentFile: x.get('contentFile').url(),
					contentTitle: x.get('contentTitle'),
					schedule: x.get('schedule'),
					updatedAt: x.get('updatedAt')
				});
				console.log('data', x);
				this.setState({
					modal: false,
					batch: []
				});
				window.location.reload(false);
			})
			.catch((err) => {
				console.log(err);
			});
	}

	handleEdit() {}

	handleUpdate() {}

	handleDelete() {}

	handleSearch(e) {
		e.preventDefault();

		console.log(this.state.batch);

		this.setState({ loading: true });

		const data = {
			batch: this.state.batch
		};

		Axios.post(baseurl('trainee/batch'), data)
			.then((x) => {
				console.log(x.data);
				this.setState({ trainee: x.data, loading: false });
			})
			.catch((err) => this.setState({ error: err, loading: false }));
	}

	render() {
		const { trainee, error, loading, batch } = this.state;
		console.log(batch);

		return (
			<div className="content">
				<ModalHandler
					show={this.state.addMode}
					title="Add trainee"
					handleHide={() => this.setState({ addMode: false })}
					body={
						<Form onSubmit={this.handleUpdate}>
							<Form.Group controlId="formCategory">
								<Form.Label>Content title</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									type="text"
									value={1}
									placeholder="Enter content title"
									onChange={() => {}}
								/>
							</Form.Group>

							<Form.Group controlId="formSubtitle">
								<Form.Label>Content body</Form.Label>
								<Form.Control
									autoCapitalize="true"
									as="textarea"
									//maxLength={300}
									autoComplete="false"
									type="text"
									value={1}
									placeholder="Enter content body"
									onChange={this.handleSubtitle}
								/>
							</Form.Group>

							<Form.Group>
								<Form.File
									id="exampleFormControlFile1"
									label="Content image"
									onChange={(e) => {
										this.setState({ contentImage: e.target.files[0] });
										console.log(e.target.files[0]);
									}}
								/>
							</Form.Group>

							<Form.Group>
								<Form.Label>Schedule</Form.Label>
								<DateTime
