import React, { Component } from 'react';
import { Form, Col, Row } from 'react-bootstrap';

class Quiz extends Component {
	constructor(props) {
		super(props);
	}
	shuffle(array) {
		var currentIndex = array.length,
			temporaryValue,
			randomIndex;

		// While there remain elements to shuffle...
		while (0 !== currentIndex) {
			// Pick a remaining element...
			randomIndex = Math.floor(Math.random() * currentIndex);
			currentIndex -= 1;

			// And swap it with the current element.
			temporaryValue = array[currentIndex];
			array[currentIndex] = array[randomIndex];
			array[randomIndex] = temporaryValue;
		}

		return array;
	}
	render() {
		const myQuestions = [
			{
				question: 'Who invented JavaScript?',
				answers: {
					a: 'Douglas Crockford',
					b: 'Sheryl Sandberg',
					c: 'Brendan Eich'
				},
				correctAnswer: 'c'
			},
			{
				question: 'Which one of these is a JavaScript package manager?',
				answers: {
					a: 'Node.js',
					b: 'TypeScript',
					c: 'npm'
				},
				correctAnswer: 'c'
			},
			{
				question: 'Which tool can you use to ensure code quality?',
				answers: {
					a: 'Angular',
					b: 'jQuery',
					c: 'RequireJS'
				},
				correctAnswer: 'c'
			}
		];

		let answerKey = [];

		const getKey = () => {
			myQuestions.map((type, i) => {
				for (let p in type) {
					answerKey.push(p);
				}
			});
		};

		return (
			<Form.Group as={Row}>
				<Form.Label as="legend" column sm={2}>
					Radios
				</Form.Label>
				<Col sm={10}>
					{myQuestions.map(({ question, answers }, i) => (
						<div>
							{myQuestions[0].question}
							<Form.Check
								type="radio"
								label={answers.a}
								name="formHorizontalRadios"
								id={i}
							/>
							<Form.Check
								type="radio"
								label={answers[0].b}
								name="formHorizontalRadios"
								id={i}
							/>
							<Form.Check
								type="radio"
								label={answers[0].c}
								name="formHorizontalRadios"
								id={i}
							/>
						</div>
					))}
				</Col>
			</Form.Group>
			// <Form>
			// 	{myQuestions.map(({ answers, question }, index) => {
			// 		return Object.keys(answers).map((key, i) => {
			// 			return (
			// 				<div key={i} className="mb-3">
			// 					<Form.Group>
			// 						<Col sm={10}>
			// 							<Form.Check
			// 								onClick={(e) => console.log(e.target.value)}
			// 								type="radio"
			// 								value={key}
			// 								id={i}
			// 								label={answers[key]}
			// 							/>
			// 						</Col>
			// 					</Form.Group>
			// 				</div>
			// 			);
			// 		});
			// 		// Object.keys(answers).forEach((key, i) => {
			// 		// 	<div className="mb-3">
			// 		// 		<Form.Check
			// 		// 			onClick={(e) => console.log(e.target.value)}
			// 		// 			type="radio"
			// 		// 			value={key}
			// 		// 			id={`default-radio`}
			// 		// 			label={answers[key]}
			// 		// 		/>
			// 		// 	</div>;
			// 		// });
			// 	})}
			// </Form>
		);
	}
}

export default Quiz;
