* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React, { Component } from 'react';
import axios from 'axios';
import { Container, Row, Col, Table, Tooltip, Button, OverlayTrigger } from 'react-bootstrap';

import Card from 'components/Card/Card.jsx';
import { thArray, tdArray } from 'variables/Variables.jsx';
import { Link } from 'react-router-dom';
import moment from 'moment';

class QuizList extends Component {
	constructor(props) {
		super(props);
		this.state = {
			reco: [],
			trainee: [],
			loading: false
		};
		this.handleAddQuestion = this.handleAddQuestion.bind(this);
	}

	componentDidMount() {
		this.getData();
	}

	handleAddQuestion(a, b) {
		let x = a + b;
		console.log('clicked', x);
		this.setState({ loading: true });
	}

	baseUrl(route) {
		return `http://localhost:5000/api/${route}`;
	}

	convertAnswer(answer) {
		switch (answer) {
			case 0:
				return 'A';
				break;
			case 1:
				return 'B';
				break;
			case 2:
				return 'C';
				break;
			case 3:
				return 'D';
				break;
		}
	}

	getData() {
		const data = {
			categoryId: this.props.match.params.id
		};
		return axios.post(this.baseUrl('quiz/category'), data).then((response) => {
			console.log(response.data);
			this.setState({ reco: response.data });
		});
	}

	render() {
		const FORMAT = 'DD/MM/YYYY';
		const FORMAT2 = 'H:mm:ss';
		const { reco, loading, trainee } = this.state;
		console.log(reco.length);
		const tooltip = <Tooltip id="button-tooltip">Simple tooltip</Tooltip>;
		return (
			<div className="content">
				<Container fluid>
					<Row>
						<Col md={12}>
							<Card
								category={
									<Link to={`/trainee/add`}>
										<button
											type="button"
											className="btn btn-labeled btn-primary"
											onClick={() => {
												this.handleAddQuestion(1, 2);
											}}
										>
											<span className="btn-label">
												<i className="glyphicon glyphicon-plus" />
											</span>{' '}
											{loading ? 'Loading...' : 'Add Trainee'}
										</button>
									</Link>
								}
								ctTableFullWidth
								ctTableResponsive
								content={
									<Table striped hover>
										<thead>
											<tr>
												<th>NO</th>
												<th>CATEGORY</th>
												<th>QUESTION</th>
												<th>OPTION</th>
												<th>JAWABAN</th>
												<th>SCHEDULE</th>
												<th>START TIME</th>
												<th>END TIME</th>
												<th>ACTION</th>
											</tr>
										</thead>
										<tbody key={1}>
											{reco.map((prop, key) => (
												<tr key={key}>
													<td>{key + 1}.</td>
													<td>{prop.categoryId.category}</td>
													<td>{prop.question}</td>
													<td>
														{prop.answers.map((sult, i) => (
															<li>{sult}</li>
														))}
													</td>
													<td>
														{() =>
															this.convertAnswer(prop.correctAnswers)}
													</td>
													<td>
														{moment(prop.schedule.iso).format(FORMAT)}
													</td>
													<td>
														{moment(prop.startTime.iso).format(FORMAT2)}
													</td>
													<td>
														{moment(prop.endTime.iso).format(FORMAT2)}
													</td>
													<td>
														<Link to={`quiz/${prop.objectId}/view`}>
															<button
																type="button"
																className="btn btn-primary btn-circle mr-2"
															>
																<i className="fa fa-eye" />
															</button>
														</Link>{' '}
														<Link to={`quiz/${prop.objectId}/edit`}>
															<button
																type="button"
																className="btn btn-warning btn-circle"
															>
																<i className="fa fa-pencil" />
															</button>
														</Link>{' '}
														<Link to={`quiz/${prop.objectId}/delete`}>
															<button
																type="button"
																className="btn btn-danger btn-circle"
															>
																<i className="fa fa-trash" />
															</button>
														</Link>
													</td>
												</tr>
											))}
										</tbody>
									</Table>
								}
							/>
						</Col>
					</Row>
				</Container>
			</div>
		);
	}
}

export default QuizList;
