* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React, { Component } from 'react';
import axios from 'axios';
import {
	Container,
	Row,
	Col,
	Table,
	Tooltip,
	Button,
	OverlayTrigger,
	Modal,
	Form
} from 'react-bootstrap';

import Card from 'components/Card/Card.jsx';
import { thArray, tdArray } from 'variables/Variables.jsx';
import { Link } from 'react-router-dom';
import moment from 'moment';
import e from 'cors';
import ModalHandler from './ModalHandler';

class QuizList extends Component {
	constructor(props) {
		super(props);
		this.state = {
			reco: [],
			question: '',
			correctAnswers: 0,
			answers: [],
			trainee: [],
			optionA: '',
			optionB: '',
			optionC: '',
			optionD: '',
			loading: false,
			addModal: false
		};
		this.handleAddQuestion = this.handleAddQuestion.bind(this);
	}

	componentDidMount() {
		this.getData();
	}

	handleAddQuestion(a, b) {}

	baseUrl(route) {
		return `http://localhost:5000/api/${route}`;
	}

	convertAnswer(answer) {
		switch (answer) {
			case 0:
				return 'A';
				break;
			case 1:
				return 'B';
				break;
			case 2:
				return 'C';
				break;
			case 3:
				return 'D';
				break;
		}
	}

	handleMergeOption() {
		const { optionA, optionB, optionC, optionD } = this.state;
		return [ optionA, optionB, optionC, optionD ];
	}

	handleAdd() {
		const { question, answers, correctAnswers } = this.state;
		const data = {
			question,
			option: answers,
			correctAnswers
		};
	}

	getData() {
		const data = {
			categoryId: this.props.match.params.id
		};
		return axios.post(this.baseUrl('quiz/category'), data).then((response) => {
			console.log(response.data);
			this.setState({ reco: response.data });
		});
	}

	render() {
		const FORMAT = 'DD/MM/YYYY';
		const FORMAT2 = 'H:mm:ss';
		const {
			reco,
			loading,
			trainee,
			addModal,
			question,
			correctAnswers,
			answers,
			optionA,
			optionB,
			optionC,
			optionD
		} = this.state;
		const tooltip = <Tooltip id="button-tooltip">Simple tooltip</Tooltip>;

		return (
			<div className="content">
				<ModalHandler
					show={this.state.addModal}
					title="Add quiz"
					handleHide={() => this.setState({ addModal: false })}
					body={
						<Form onSubmit={this.handleSubmit}>
							<Form.Group controlId="formCategory">
								<Form.Label>Pertanyaan</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									type="text"
									value={question}
									placeholder="Enter question"
									onChange={(e) => {
										this.setState({ question: e.target.value });
									}}
								/>
							</Form.Group>

							<Form.Group controlId="formSubtitle">
								<Form.Label>Option A</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									type="text"
									value={optionA}
									placeholder="Option A"
									onChange={(e) => {
										this.setState({
											optionA: e.target.value
										});
									}}
								/>
							</Form.Group>

							<Form.Group controlId="formSubtitleB">
								<Form.Label>Option B</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									type="text"
									value={optionB}
									placeholder="Option B"
									onChange={(e) => {
										this.setState({
											optionB: e.target.value
										});
									}}
								/>
							</Form.Group>

							<Form.Group controlId="formSubtitleC">
								<Form.Label>Option C</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									type="text"
									value={optionC}
									placeholder="Option C"
									onChange={(e) => {
										this.setState({
											optionC: e.target.value
										});
									}}
								/>
							</Form.Group>

							<Form.Group controlId="formSubtitleD">
								<Form.Label>Option D</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									type="text"
									value={optionD}
									placeholder="Option D"
									onChange={(e) => {
										this.setState({
											optionD: e.target.value
										});

										console.log(this.state.answers);
									}}
								/>
							</Form.Group>

							<Form.Group controlId="formGridState">
								<Form.Label>Jawaban</Form.Label>
								<Form.Control
									as="select"
									onChange={(e) => {
										this.setState({ correctAnswers: e.target.value });
										console.log(e.target.value);
									}}
								>
									<option value={0}>A</option>
									<option value={1}>B</option>
									<option value={2}>C</option>
									<option value={3}>D</option>
								</Form.Control>
							</Form.Group>

							<Button variant="primary" type="submit">
								Submit
							</Button>
						</Form>
					}
				/>
				{/* <Modal show={modal} onHide={() => this.setState({ modal: false })}>
					<Modal.Header closeButton>
						<Modal.Title>Modal heading</Modal.Title>
					</Modal.Header>
					<Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
					<Modal.Footer>
						<Button variant="secondary" onClick={() => this.setState({ modal: false })}>
