import React, { Component } from 'react';
import {
	Container,
	Row,
	Col,
	FormGroup,
	ControlLabel,
	FormControl,
	Form,
	Tooltip,
	OverlayTrigger
} from 'react-bootstrap';

import { Card } from 'components/Card/Card.jsx';
import { FormInputs } from 'components/FormInputs/FormInputs.jsx';
import { UserCard } from 'components/UserCard/UserCard.jsx';
import Button from 'components/CustomButton/CustomButton.jsx';

import avatar from 'assets/img/faces/face-3.jpg';
import Axios from 'axios';
import { baseurl } from 'utils/baseurl';
import Parse from 'parse';
import ModalHandler from './Category/ModalHandler';
import moment from 'moment';
import DateTime from 'react-datetime';
import { Link } from 'react-router-dom';

class UserProfile extends Component {
	constructor(props) {
		super(props);
		this.state = {
			trainee: [],
			izin: [],
			error: '',
			userId: '',
			userIndex: 0,
			loading: false,
			addMode: false,
			editMode: false,
			deleteMode: false,
			buttonLoading: false
		};

		this.handleApprove = this.handleApprove.bind(this);
		this.handleReject = this.handleReject.bind(this);
	}

	componentDidMount() {
		this.getTrainee();
	}

	handleApprove(e) {
		const Late = Parse.Object.extend('Late');
		const query = new Parse.Query(Late);

		query.get(this.state.userId).then((x) => {
			x.set('status', 1);
			x.save().then(() => {
				const newOvertime = [ ...this.state.late ];
				newOvertime.splice(this.state.userIndex, 1);
				this.setState({
					overtime: newOvertime,
					editMode: false
				});
			});
		});
	}

	handleReject(e) {
		const Late = Parse.Object.extend('Late');
		const query = new Parse.Query(Late);

		query.get(this.state.userId).then((x) => {
			x.set('status', 0);
			x.save().then(() => {
				const newOvertime = [ ...this.state.late ];
				newOvertime.splice(this.state.userIndex, 1);
				this.setState({
					overtime: newOvertime,
					deleteMode: false
				});
			});
		});
	}

	getTrainee() {
		this.setState({ loading: true });
		const Izin = Parse.Object.extend('Izin');
		const Leader = Parse.Object.extend('Leader');
		const leader = new Leader();
		const query = new Parse.Query(Izin);

		console.log(Parse.User.current().get('leaderId').id);

		leader.id = Parse.User.current().get('leaderId').id;
		query.equalTo('leaderId', leader);
		query.find().then((x) => {
			console.log(x);
			this.setState({ izin: x, loading: false });
		});
	}

	render() {
		const { izin, error, loading, batch } = this.state;
		const {
			username,
			email,
			password,
			passwordConf,
			dob,
			pob,
			phoneNumber,
			fullname,
			profile
		} = this.state;
		const tooltip = (msg) => <Tooltip id="button-tooltip">{msg}</Tooltip>;

		return (
			<div className="content">
				<ModalHandler
					show={this.state.deleteMode}
					title="Delete confirmation"
					handleHide={() => this.setState({ deleteMode: false })}
					handleSave={this.handleDelete}
					body={'Delete trainee ' + this.state.fullnames + ' ?'}
				/>
				<ModalHandler
					show={this.state.editMode}
					title="Edit content"
					handleHide={() => this.setState({ editMode: false })}
					body={
						<Form onSubmit={this.handleUpdate}>
							<Form.Group controlId="formCategory">
								<Form.Label>Fullname</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									value={fullname}
									type="text"
									placeholder="Enter fullname"
									onChange={(e) => this.setState({ fullname: e.target.value })}
								/>
							</Form.Group>

							<Form.Group controlId="formCategory">
								<Form.Label>Username</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									value={username}
									type="text"
									placeholder="Enter fullname"
									onChange={(e) => this.setState({ username: e.target.value })}
								/>
							</Form.Group>

							<Form.Group controlId="formSubtitle">
								<Form.Label>Email</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									value={email}
									type="email"
									placeholder="Enter email"
									onChange={(e) => this.setState({ email: e.target.value })}
								/>
							</Form.Group>

							<Form.Group>
								<Form.File
									id="exampleFormControlFile1"
									label="Trainee picture"
									onChange={(e) => {
										this.setState({ profile: e.target.files[0] });
										console.log(e.target.files[0]);
									}}
								/>
							</Form.Group>

							<Form.Group controlId="formCategory">
								<Form.Label>Tempat lahir</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									value={pob}
									type="text"
									placeholder="Tempat lahir"
									onChange={(e) => this.setState({ pob: e.target.value })}
								/>
							</Form.Group>

							<Form.Group>
								<Form.Label>Tanggal lahir</Form.Label>
								<DateTime
									timeFormat={false}
									value={this.state.dob}
									onChange={(momentz) => {
										console.log(momentz.toDate());
										this.setState({
											dob: momentz.format('DD/MM/YYYY')
										});
									}}
									inputProps={{
										placeholder: 'Select date',
										readOnly: true
									}}
								/>
							</Form.Group>

							<Form.Group controlId="formGridState">
								<Form.Label>Batch</Form.Label>
								<Form.Control
									as="select"
									onChange={(e) => {
										this.setState({ batch: e.target.value });
										console.log(e.target.value);
									}}
								>
									{[ 1, 2, 3 ].map((x) => (
										<option value={x}>{`Batch ${x}`}</option>
									))}
								</Form.Control>
							</Form.Group>

							<Form.Group controlId="formPhone">
								<Form.Label>Phone number</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									type="number"
									value={phoneNumber}
									placeholder="Nomor telfon"
									onChange={(e) => this.setState({ phoneNumber: e.target.value })}
								/>
							</Form.Group>

							<Button variant="primary" type="submit">
								{this.state.loading ? 'Processing...' : 'Submit'}
							</Button>
						</Form>
					}
				/>
				<ModalHandler
					show={this.state.addMode}
					title="Add trainee"
					handleHide={() => this.setState({ addMode: false })}
					body={
						<Form onSubmit={this.handleAdd}>
							<Form.Group controlId="formCategory">
								<Form.Label>Fullname</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									type="text"
									placeholder="Enter fullname"
									onChange={(e) => this.setState({ fullname: e.target.value })}
								/>
							</Form.Group>

							<Form.Group controlId="formCategory">
								<Form.Label>Username</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									type="text"
									placeholder="Enter fullname"
									onChange={(e) => this.setState({ username: e.target.value })}
								/>
							</Form.Group>

							<Form.Group controlId="formSubtitle">
								<Form.Label>Email</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									type="email"
									placeholder="Enter email"
									onChange={(e) => this.setState({ email: e.target.value })}
								/>
							</Form.Group>

							<Form.Group controlId="formPasw">
								<Form.Label>Password</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									type="text"
									placeholder="Enter password"
									onChange={(e) => this.setState({ password: e.target.value })}
								/>
							</Form.Group>

							<Form.Group>
								<Form.File
									id="exampleFormControlFile1"
									label="Trainee picture"
									onChange={(e) => {
										this.setState({ profile: e.target.files[0] });
										console.log(e.target.files[0]);
									}}
								/>
							</Form.Group>

							<Form.Group controlId="formCategory">
								<Form.Label>Tempat lahir</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									type="text"
									placeholder="Tempat lahir"
									onChange={(e) => this.setState({ pob: e.target.value })}
								/>
							</Form.Group>

							<Form.Group>
								<Form.Label>Tanggal lahir</Form.Label>
								<DateTime
									timeFormat={false}
									value={this.state.dob}
									onChange={(momentz) => {
										console.log(momentz.toDate());
										this.setState({
											dob: momentz.format('DD/MM/YYYY')
										});
									}}
									inputProps={{
										placeholder: 'Select date',
										readOnly: true
									}}
								/>
							</Form.Group>

							<Form.Group controlId="formGridState">
								<Form.Label>Batch</Form.Label>
								<Form.Control
									as="select"
									onChange={(e) => {
										this.setState({ batch: e.target.value });
										console.log(e.target.value);
									}}
								>
									{[ 1, 2, 3 ].map((x) => (
										<option value={x}>{`Batch ${x}`}</option>
									))}
								</Form.Control>
							</Form.Group>

							<Form.Group controlId="formPhone">
								<Form.Label>Phone number</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									type="number"
									//value={1}
									placeholder="Nomor telfon"
									onChange={(e) => this.setState({ phoneNumber: e.target.value })}
								/>
							</Form.Group>

							<Button variant="primary" type="submit">
								{this.state.loading ? 'Processing...' : 'Submit'}
							</Button>
						</Form>
					}
				/>
				<Container fluid>
					<Row>
						<Col md={12}>
							<Card
								title="Request izin"
								content={
									<div>
										<Row>
											{izin.length < 1 ? (
												<Col md={12}>No data found...</Col>
											) : (
												izin.map((x, i) => (
													<Col md={3}>
														<UserCard
															out={x.status}
															bgImage={
																<img
																	src="https://ununsplash.imgix.net/photo-1431578500526-4d9613015464?fit=crop&fm=jpg&h=300&q=75&w=400"
																	alt="..."
																/>
															}
															avatar={
																x.attributes.attachFile ==
																undefined ? (
																	'https://ununsplash.imgix.net/photo-1431578500526-4d9613015464?fit=crop&fm=jpg&h=300&q=75&w=400'
																) : (
																	x.attributes.attachFile.url()
																)
															}
															name={x
																.get('fullname')
																.split(' ')
																.slice(0, 2)
																.join(' ')}
															userName={x.get('descIzin')}
															description={
																<span>
																	Alasan :{' '}
																	{x.get('alasanIzin') !== '-' ||
																	x.get('alasanIzin') ==
																		undefined ? (
																		x.get('alasanIzin')
																	) : (
																		'Izin terlampir'
																	)}
																	{/* <br />
																	{x.dateOfBirth}
																	<br />
																	{x.phoneNumber} */}
																</span>
															}
															socials={
																<div>
																	{/* <OverlayTrigger
																		placement="right"
																		overlay={tooltip(
																			'Lihat pengerjaan quiz'
																		)}
																	>
																		<Link
																			to={`/admin/quiz/${x.objectId}`}
																		>
																			<Button simple>
																				<i className="fa fa-eye" />
																			</Button>
																		</Link>
																	</OverlayTrigger> */}
																	<OverlayTrigger
																		placement="right"
																		overlay={tooltip('Approve')}
																	>
																		<Button
																			simple
																			onClick={() => {
																				this.setState({
																					editMode: true,
																					userId: x.id,
																					userIndex: i,
																					fullnames: x.get(
																						'fullname'
																					)
																				});
																			}}
																		>
																			<i className="fa fa-check" />
																		</Button>
																	</OverlayTrigger>
																	<OverlayTrigger
																		placement="right"
																		overlay={tooltip('Reject')}
																	>
																		<Button
																			simple
																			onClick={(e) => {
																				this.setState({
																					editMode: true,
																					userId: x.id,
																					userIndex: i,
																					fullnames: x.get(
																						'fullname'
																					)
																				});
																			}}
																		>
																			<i className="fa fa-close" />
																		</Button>
																	</OverlayTrigger>
																</div>
															}
														/>
													</Col>
												))
											)}
										</Row>
									</div>
								}
							/>
						</Col>
					</Row>

					{/* </Col> */}
					{/* </Row> */}
				</Container>
			</div>
		);
	}
}

export default UserProfile;
