import React, { Component } from 'react';
import {
	Container,
	Row,
	Col,
	OverlayTrigger,
	Tooltip,
	Button,
	Modal,
	Form,
	FormCheck
} from 'react-bootstrap';
import Card from 'components/Card/Card';
import ChartistGraph from 'react-chartist';
import Axios from 'axios';
import moment from 'moment';
import { Link } from 'react-router-dom';
import DateTime from 'react-datetime';
import './datepicker.css';

class MasterCategory extends Component {
	constructor(props) {
		super(props);
		this.state = {
			data: {
				quizCategory: null,
				subtitle: null,
				desc: null,
				schedule: moment().format('DD/MM/YYYY [at] H:mm:ss'),
				timeInMinutes: null,
				batch: []
			},
			batch: [],
			category: [],
			loading: false,
			modal: false,
			moment: moment().format('DD/MM/YYYY [at] H:mm:ss')
		};
		this.handleDate = this.handleDate.bind(this);
	}

	componentDidMount() {
		this.getCategory();
	}

	handleDate(moment) {
		this.setState((prevState) => ({
			data: {
				...prevState.data,
				schedule: moment.format('DD/MM/YYYY [at] H:mm:ss')
			}
		}));
	}

	handleDaySelect(event) {
		const { batch } = this.state;
		let batchList = batch;
		let check = event.target.checked;
		let checkedBatch = event.target.value;
		if (check) {
			console.log(check);
			this.setState((prevState) => ({
				data: {
					...prevState.data,
					batch: [ ...this.state.data.batch, checkedBatch ]
				}
			}));
		} else {
			var index = batchList.indexOf(checkedBatch);
			if (index > -1) {
				batchList.splice(index, 1);
				this.setState((prevState) => ({
					data: {
						...prevState.data,
						batch: batchList
					}
				}));
			}
		}
	}

	getCategory() {
		this.setState({ loading: true });

		const url = 'http://localhost:5000/api/category/list';

		return Axios.get(url)
			.then((response) => {
				this.setState({ category: response.data, loading: false });
			})
			.catch((err) => console.log(err));
	}

	render() {
		const { category, loading, modal } = this.state;
		const { quizCategory, batch, desc, timeInMinutes, subtitle, schedule } = this.state.data;
		const remove = <Tooltip id="remove_tooltip">Remove</Tooltip>;

		return (
			<div className="content">
				<Modal
					scrollable={true}
					show={modal}
					onHide={() => this.setState({ modal: false })}
				>
					<Modal.Header closeButton>
						<Modal.Title
							style={{
								marginTop: '0px',
								marginBottom: '0px',
								color: 'Black',
								fontWeight: 'bold'
							}}
						>
							Add Quiz Category
						</Modal.Title>
					</Modal.Header>
					<Modal.Body>
						<Form>
							<Form.Group controlId="formCategory">
								<Form.Label>Quiz category</Form.Label>
								<Form.Control
									type="text"
									placeholder="Enter category"
									onChange={(e) => {
										this.setState((prevState) => ({
											data: {
												...prevState.data,
												quizCategory: e.target.value
											}
										}));
									}}
								/>
							</Form.Group>

							<Form.Group controlId="formSubtitle">
								<Form.Label>Subtitle</Form.Label>
								<Form.Control
									type="text"
									placeholder="Enter subtitle"
									onChange={(e) => {
										this.setState((prevState) => ({
											data: {
												...prevState.data,
												subtitle: e.target.value
											}
										}));
									}}
								/>
							</Form.Group>

							<Form.Group controlId="formDescription">
								<Form.Label>Description</Form.Label>
								<Form.Control
									as="textarea"
									placeholder="Enter description"
									rows={5}
									onChange={(e) => {
										this.setState((prevState) => ({
											data: {
												...prevState.data,
												desc: e.target.value
											}
										}));
									}}
								/>
							</Form.Group>

							<Form.Group>
								<Form.Label>Schedule</Form.Label>
								<DateTime
									value={this.state.data.schedule}
									onChange={this.handleDate}
									inputProps={{
										placeholder: 'Select schedule',
										readOnly: true
									}}
								/>
							</Form.Group>

							<Form.Group>
								<Form.Label>Waktu pengerjaan (menit)</Form.Label>
								<Form.Control
									type="number"
									placeholder="Waktu pengerjaan"
									onChange={(e) => {
										this.setState((prevState) => ({
											data: {
												...prevState.data,
												timeInMinutes: e.target.value
											}
										}));
									}}
								/>
							</Form.Group>

							<Form.Group controlId="formBasicCheckbox">
								<Form.Check
									inline
									type="checkbox"
									value={1}
									label="Batch 1"
									onChange={this.handleDaySelect}
								/>
								<Form.Check
									inline
									type="checkbox"
									value={2}
									label="Batch 2"
									onChange={this.handleDaySelect}
								/>
								<Form.Check
									inline
									type="checkbox"
									value={3}
									label="Batch 3"
									onChange={this.handleDaySelect}
								/>
							</Form.Group>
							<Button variant="primary" type="submit">
								Submit
							</Button>
						</Form>
					</Modal.Body>
					<Modal.Footer>
						<Button variant="secondary" onClick={() => this.setState({ modal: false })}>
							Close
						</Button>
						<Button variant="primary" onClick={() => this.setState({ modal: false })}>
							Save Changes
						</Button>
					</Modal.Footer>
				</Modal>
				<Container fluid>
					<Row>
						<Col md={12}>
							<Card
								category={
									<button
										type="button"
										className="btn btn-labeled btn-primary"
										onClick={() => {
											this.setState({ modal: true });
										}}
									>
										<span className="btn-label">
											<i className="fa fa-plus" />
										</span>{' '}
										Add Category
									</button>
								}
								content={
