import React, { Component } from 'react';
import {
	Container,
	Row,
	Col,
	FormGroup,
	ControlLabel,
	FormControl,
	Form,
	Tooltip,
	OverlayTrigger,
	Table
} from 'react-bootstrap';

import { Card } from 'components/Card/Card.jsx';
import { FormInputs } from 'components/FormInputs/FormInputs.jsx';
import { UserCard } from 'components/UserCard/UserCard.jsx';
import Button from 'components/CustomButton/CustomButton.jsx';

import avatar from 'assets/img/faces/face-3.jpg';
import Axios from 'axios';
import { baseurl } from 'utils/baseurl';
import Parse from 'parse';
import ModalHandler from './Category/ModalHandler';
import moment from 'moment';
import DateTime from 'react-datetime';
import { Link } from 'react-router-dom';
import { handleConvert } from 'utils/converter';

class Overtime extends Component {
	constructor(props) {
		super(props);
		this.state = {
			trainee: [],
			overtime: [],
			error: '',
			userId: '',
			fullnames: '',
			status: 3,
			userIndex: 0,
			loading: false,
			addMode: false,
			editMode: false,
			deleteMode: false,
			buttonLoading: false,
			checkAll: false,
			checkId: [],
			checkOne: false,
			lampiran: '',
			photoMode: false
		};

		this.handleSearch = this.handleSearch.bind(this);
		this.handleDelete = this.handleDelete.bind(this);
		this.handleApprove = this.handleApprove.bind(this);
		this.handleReject = this.handleReject.bind(this);
		this.handleFilter = this.handleFilter.bind(this);
		this.handleAllCheck = this.handleAllCheck.bind(this);
		this.handleChildCheck = this.handleChildCheck.bind(this);
	}

	componentDidMount() {
		this.getTrainee();
	}

	getTrainee() {
		this.setState({ loading: true });
		const Overtime = Parse.Object.extend('Overtime');
		const Leader = Parse.Object.extend('Leader');
		const leader = new Leader();
		const query = new Parse.Query(Overtime);

		const d = new Date();
		const start = new moment(d);
		start.startOf('day');
		const finish = new moment(start);
		finish.add(1, 'day');

		console.log(Parse.User.current().get('leaderId').id);

		leader.id = Parse.User.current().get('leaderId').id;
		query.equalTo('leaderId', leader);
		query.equalTo('status', 3);
		query.greaterThanOrEqualTo('createdAt', start.toDate());
		query.lessThan('createdAt', finish.toDate());
		query.find().then((x) => {
			console.log(x);
			this.setState({ overtime: x, loading: false });
		});
	}

	getTraineeHistory() {
		this.setState({ loading: true });
		const Overtime = Parse.Object.extend('Overtime');
		const Leader = Parse.Object.extend('Leader');
		const leader = new Leader();
		const query = new Parse.Query(Overtime);

		const d = new Date();
		const start = new moment(d);
		start.startOf('day');
		const finish = new moment(start);
		finish.add(1, 'day');

		console.log(Parse.User.current().get('leaderId').id);

		leader.id = Parse.User.current().get('leaderId').id;
		query.equalTo('leaderId', leader);
		query.equalTo('status', 3);
		// query.greaterThanOrEqualTo('createdAt', start.toDate());
		// query.lessThan('createdAt', finish.toDate());
		query.find().then((x) => {
			console.log(x);
			this.setState({ overtime: x, loading: false });
		});
	}

	handleFilter(e) {
		e.preventDefault();
		this.setState({ loading: true });
		console.log(this.state.status);

		const Overtime = Parse.Object.extend('Overtime');
		const Leader = Parse.Object.extend('Leader');
		const leader = new Leader();
		const query = new Parse.Query(Overtime);

		// const d = new Date();
		// const start = new moment(d);
		// start.startOf('day');
		// const finish = new moment(start);
		// finish.add(1, 'day');

		// console.log(Parse.User.current().get('leaderId').id);

		// leader.id = Parse.User.current().get('leaderId').id;
		// query.equalTo('leaderId', leader);
		// query.equalTo('status', parseInt(this.state.status));
		// // query.greaterThanOrEqualTo('createdAt', start.toDate());
		// // query.lessThan('createdAt', finish.toDate());
		// query.find().then((x) => {
		// 	console.log(x);
		// 	this.setState({ overtime: x, loading: false });
		// });

		if (this.state.statusCalendar == 4) {
			// const d = new Date();
			const start = new moment(this.state.startDate);
			start.startOf('day');
			const finish = new moment(start);
			finish.add(1, 'day');

			console.log(Parse.User.current().get('leaderId').id);

			leader.id = Parse.User.current().get('leaderId').id;
			query.equalTo('leaderId', leader);
			query.equalTo('status', parseInt(this.state.status));
			query.greaterThanOrEqualTo('time', start.toDate());
			query.lessThan('time', finish.toDate());
			query.find().then((x) => {
				console.log(x);
				this.setState({ overtime: x, loading: false });
			});
		} else if (this.state.statusCalendar == 5) {
			const start = new moment(this.state.startDate);
			start.startOf('week');
			const finish = new moment(start);
			finish.add(1, 'week');
			// const startDate = moment(this.state.startDate).startOf('isoWeek').toDate();
			// // const endDate = moment(this.state.endDate).add(7, 'day').endOf('isoWeek').toDate();
			// const endDate = moment(this.state.endDate).endOf('isoWeek').toDate();
			leader.id = Parse.User.current().get('leaderId').id;
			query.equalTo('leaderId', leader);
			query.equalTo('status', parseInt(this.state.status));
			query.greaterThanOrEqualTo('time', start.toDate());
			query.lessThan('time', finish.toDate());
			query.find().then((x) => {
				console.log(x);
				this.setState({ overtime: x, loading: false });
			});
		} else if (this.state.statusCalendar == 6) {
			// const startDate = moment(this.state.startDate).startOf('month').toDate();
			// // const endDate = moment(this.state.endDate).add(7, 'day').endOf('month').toDate();
			// const endDate = moment(this.state.endDate).endOf('month').toDate();
			const start = new moment(this.state.startDate);
			start.startOf('month');
			const finish = new moment(start);
			finish.add(1, 'month');

			leader.id = Parse.User.current().get('leaderId').id;
			query.equalTo('leaderId', leader);
			query.equalTo('status', parseInt(this.state.status));
			query.greaterThanOrEqualTo('time', start.toDate());
			query.lessThan('time', finish.toDate());
			query.find().then((x) => {
				console.log(x);
				this.setState({ overtime: x, loading: false });
			});
		} else {
			const d = new Date();
			const start = new moment(d);
			start.startOf('day');
			const finish = new moment(start);
			finish.add(1, 'day');

			console.log(Parse.User.current().get('leaderId').id);

			leader.id = Parse.User.current().get('leaderId').id;
			query.equalTo('leaderId', leader);
			query.equalTo('status', parseInt(this.state.status));
			// query.greaterThanOrEqualTo('createdAt', start.toDate());
			// query.lessThan('createdAt', finish.toDate());
			query.find().then((x) => {
				console.log(x);
				this.setState({ overtime: x, loading: false });
			});
		}
	}

	approveChecked = (e) => {
		const { checkId } = this.state;
		const checkIdLength = checkId.length;
		const Izin = Parse.Object.extend('Overtime');
		const query = new Parse.Query(Izin);
		let totalData = 0;

		checkId.map((id) => {
			this.handleApproveAll(id);
		});
	};

	rejectChecked = (e) => {
		const { checkId } = this.state;

		checkId.map((id) => {
			this.handleRejectAll(id);
		})
	};

	handleApprove(e) {
		this.setState({ loading: true });
		const Overtime = Parse.Object.extend('Overtime');
		const query = new Parse.Query(Overtime);

		query.get(this.state.userId).then((x) => {
			x.set('status', 1);
			x.save().then(() => {
				const newOvertime = [...this.state.overtime];
				newOvertime.splice(this.state.userIndex, 1);
				this.setState({
					overtime: newOvertime,
					editMode: false,
					loading: false
				});
			});
		});
	}

	handleApproveAll(e) {
		this.setState({ loading: true });
		const Overtime = Parse.Object.extend('Overtime');
		const query = new Parse.Query(Overtime);

		query.get(e).then((x) => {
			x.set('status', 1);
			x.save().then(() => {
				const newOvertime = [...this.state.overtime];
				newOvertime.splice(this.state.userIndex, 1);
				this.setState({
					overtime: newOvertime,
					editMode: false,
					loading: false
				});
			});
		});
	}

	handleReject(e) {
		this.setState({ loading: true });
		const Overtime = Parse.Object.extend('Overtime');
		const query = new Parse.Query(Overtime);

		query.get(this.state.userId).then((x) => {
			x.set('status', 0);
			x.save().then(() => {
				const newOvertime = [...this.state.overtime];
				newOvertime.splice(this.state.userIndex, 1);
				this.setState({
					overtime: newOvertime,
					deleteMode: false,
					loading: false
				});
			});
		});
	}

	handleRejectAll(e) {
		this.setState({ loading: true });
		const Overtime = Parse.Object.extend('Overtime');
		const query = new Parse.Query(Overtime);

		query.get(e).then((x) => {
			x.set('status', 0);
			x.save().then(() => {
				const newOvertime = [...this.state.overtime];
				newOvertime.splice(this.state.userIndex, 1);
				this.setState({
					overtime: newOvertime,
					deleteMode: false,
					loading: false
				});
			});
		});
	}

	handleAllCheck(e) {
		let overtime = this.state.overtime;
		let collecId = [];

		overtime.map((x) => {
			x.isChecked = e.target.checked;
			if (x.isChecked) {
				collecId.push(x.id);
			} else {
				collecId = [];
			}

			return x;
		});

		this.setState({ overtime: overtime, checkId: collecId }, () => console.log(this.state.checkId));
	}

	handleChildCheck(e) {
		let { overtime } = this.state;
		const { checkId } = this.state;
		let checked = e.target.value;
		overtime.map((x) => {
			console.log('bandingkan', x.id === e.target.value);
			if (x.id === e.target.value) {
				console.log('sama');
				x.isChecked = e.target.checked;
				if (x.isChecked) {
					this.setState(
						(prevState) => ({
							checkId: [...this.state.checkId, checked]
						}),
						() => console.log(this.state.checkId)
					);
				} else {
					const index = checkId.indexOf(checked);
					if (index > -1) {
						checkId.splice(index, 1);
						this.setState(
							(prevState) => ({
								checkId: checkId
							}),
							() => console.log(this.state.checkId)
						);
					}
				}
			}
		});

		this.setState({ overtime: overtime });
	}

	handleEdit(id) {
		const User = new Parse.User();
		const query = new Parse.Query(User);

		// Finds the user by its ID
		query
			.get(id)
			.then((user) => {
				// Updates the data we want
				this.setState({
					traineeId: id,
					editMode: true,
					username: user.get('username'),
					email: user.get('email'),
					dob: user.get('dateOfBirth'),
					pob: user.get('placeOfBirth'),
					phoneNumber: user.get('phoneNumber'),
					fullname: user.get('fullname'),
					batch: user.get('batch')
				});
			})
			.catch((err) => console.log(err));
	}

	handleUpdate(e) {
		e.preventDefault();

		this.setState({ loading: true });

		const {
			username,
			email,
			password,
			passwordConf,
			dob,
			pob,
			phoneNumber,
			fullname,
			batch,
			profile
		} = this.state;

		const User = new Parse.User();
		const query = new Parse.Query(User);

		query.get(this.state.traineeId).then((user) => {
			user.set('username', `${username}-batch${batch}`);
			user.set('email', email);
			if (profile !== '') user.set('profile', new Parse.File('profile.jpg', profile));
			user.set('dateOfBirth', dob);
			user.set('placeOfBirth', pob);
			user.set('phoneNumber', phoneNumber);
			user.set('role', 'trainee');
			user.set('fullname', fullname);
			user.set('batch', parseInt(batch));
			user.set('status', 1);
			user.set('password', password);
			user.set('out', false);
			user.save().then((z) => {
				this.setState({ loading: false });
				window.location.reload(false);
			});
		});
	}

	handleDelete() {
		this.setState({ loading: true });
		const User = new Parse.User();
		const query = new Parse.Query(User);

		query.get(this.state.traineeId).then((x) => {
			x.set('status', 0);
			x.save().then((z) => {
				this.setState({ loading: false });
				window.location.reload(false);
			});
		});
	}

	handleSearch(e) {
		e.preventDefault();

		console.log(this.state.batch);

		this.setState({ loading: true });

		const data = {
			batch: this.state.batch
		};

		Axios.post(baseurl('trainee/batch'), data)
			.then((x) => {
				console.log(x.data);
				this.setState({ trainee: x.data, loading: false });
			})
			.catch((err) => this.setState({ error: err, loading: false }));
	}

	handleConvert(key) {
		switch (key) {
			case 0:
				return 'Rejected';
			case 1:
				return 'Approved';
			case 3:
				return 'All';
			default:
				break;
		}
	}

