import React, { Component } from 'react';
import { Button, Container, Row, Col } from 'react-bootstrap';
import Card from 'components/Card/Card';
import ChartistGraph from 'react-chartist';
import Axios from 'axios';
import moment from 'moment';
import { Link } from 'react-router-dom';

class MasterCategory extends Component {
	constructor(props) {
		super(props);
		this.state = {
			category: []
		};
	}

	componentDidMount() {
		this.getCategory();
	}

	getCategory() {
		const url = 'http://localhost:5000/api/category/list';

		return Axios.get(url).then((response) => {
			this.setState({ category: response.data });
		});
	}

	render() {
		const { category } = this.state;

		return (
			<div className="content">
				<Container fluid>
					<Row>
						<Col md={12}>
							<Card
								category={
									<Link to="/quiz/add">
										<button
											type="button"
											className="btn btn-labeled btn-primary"
											onClick={() => {}}
										>
											<span className="btn-label">
												<i className="glyphicon glyphicon-plus" />
											</span>{' '}
											Add Category
										</button>
									</Link>
								}
								content={
									<Row>
										{category.map((x) => (
											<Col md={3}>
												<Card
													title={x.category}
													category={x.subtitle}
													stats={moment(x.createdAt).fromNow()}
													statsIcon="pe-7s-clock"
													content={
														<div id="chartActivity" className="">
															{x.description}
														</div>
													}
													legend={
														<div className="legend">
															<Link to="/quiz/add">
																<button
																	type="button"
																	className="btn btn-labeled btn-warning"
																	onClick={() => {}}
																>
																	<span className="btn-label">
																		<i className="glyphicon glyphicon-eye-open" />
																	</span>{' '}
																	View Detail
																</button>
															</Link>
														</div>
													}
												/>
											</Col>
										))}
									</Row>
								}
							/>
						</Col>
					</Row>
				</Container>
			</div>
		);
	}
}

export default MasterCategory;
