/*!

=========================================================
* Light Bootstrap Dashboard React - v1.3.0
=========================================================

* Product Page: https://www.creative-tim.com/product/light-bootstrap-dashboard-react
* Copyright 2019 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/light-bootstrap-dashboard-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React, { Component } from 'react';
import axios from 'axios';
import {
	Container,
	Row,
	Col,
	Table,
	Tooltip,
	Button,
	OverlayTrigger,
	Modal,
	Form
} from 'react-bootstrap';

import Card from 'components/Card/Card.jsx';
import { thArray, tdArray } from 'variables/Variables.jsx';
import { Link } from 'react-router-dom';
import moment from 'moment';
import e from 'cors';
import ModalHandler from './ModalHandler';
import Axios from 'axios';
import { baseurl } from 'utils/baseurl';

class QuizList extends Component {
	constructor(props) {
		super(props);
		this.state = {
			reco: [],
			question: '',
			correctAnswers: 0,
			answers: [],
			trainee: [],
			optionA: '',
			optionB: '',
			optionC: '',
			optionD: '',
			loading: false,
			addModal: false,
			editModal: false
		};
		this.handleAddQuestion = this.handleAddQuestion.bind(this);
		this.handleAdd = this.handleAdd.bind(this);
		this.handleMergeOption = this.handleMergeOption.bind(this);
		this.handleEdit = this.handleEdit.bind(this);
	}

	handleEdit(id) {
		const data = { quizId: id };
		Axios.post(baseurl('quiz/id'), data)
			.then(({ data }) => {
				this.setState(
					{
						editMode: true,
						question: data.question,
						optionA: data.answers[0],
						optionB: data.answers[1],
						optionC: data.answers[2],
						optionD: data.answers[3],
						correctAnswers: data.correctAnswers
					},
					() => console.log('batch', this.state.batch)
				);
			})
			.catch((err) => console.log(err));
	}

	componentDidMount() {
		this.getData();
	}

	handleAddQuestion(a, b) {}

	baseUrl(route) {
		return `http://localhost:5000/api/${route}`;
	}

	convertAnswer(answer) {
		switch (answer) {
			case 0:
				return 'A';
				break;
			case 1:
				return 'B';
				break;
			case 2:
				return 'C';
				break;
			case 3:
				return 'D';
				break;
		}
	}

	handleMergeOption() {
		const { optionA, optionB, optionC, optionD } = this.state;
		console.log(optionA);
		return [ optionA, optionB, optionC, optionD ];
	}

	handleAdd(e) {
		e.preventDefault();
		const {
			question,
			answers,
			correctAnswers,
			optionA,
			optionB,
			optionC,
			optionD
		} = this.state;
		const data = {
			question,
			option: [ optionA, optionB, optionC, optionD ],
			correctAnswer: parseInt(correctAnswers),
			categoryId: this.props.match.params.id
		};

		Axios.post(baseurl('quiz/add'), data)
			.then((x) => {
				this.setState({ addModal: false, reco: this.state.reco.concat(x.data.x) });
				console.log('success add data');
			})
			.catch((err) => console.log(err));
	}

	getData() {
		const data = {
			categoryId: this.props.match.params.id
		};
		return axios.post(this.baseUrl('quiz/category'), data).then((response) => {
			console.log(response.data);
			this.setState({ reco: response.data });
		});
	}

	render() {
		const FORMAT = 'DD/MM/YYYY';
		const FORMAT2 = 'H:mm:ss';
		const {
			reco,
			loading,
			trainee,
			addModal,
			question,
			correctAnswers,
			answers,
			optionA,
			optionB,
			optionC,
			optionD
		} = this.state;
		const tooltip = <Tooltip id="button-tooltip">Simple tooltip</Tooltip>;

		return (
			<div className="content">
				<ModalHandler
					show={this.state.editModal}
					title="Edit category"
					handleHide={() => {
						this.setState({ editModal: false });
					}}
				/>
				<ModalHandler
					show={this.state.addModal}
					title="Add quiz"
					handleHide={() => this.setState({ addModal: false })}
					body={
						<Form onSubmit={this.handleAdd}>
							<Form.Group controlId="formCategory">
								<Form.Label>Pertanyaan</Form.Label>
								<Form.Control
									autoCapitalize="true"
									autoComplete="false"
									type="text"
									value={question}
									placeholder="Enter question"
									onChange={(e) => {
