import React, { Component } from 'react';
import axios from 'axios';
import { Form, Col, FormControl, FormGroup, ControlLabel, Checkbox, Button } from 'react-bootstrap';

class Login extends Component {
	state = {
		hello: '',
		username: '',
		password: ''
	};

	componentDidMount() {
		this.getHelloWorld();
	}

	handleLogin = () => {
		const url = 'http://localhost:5000/login';
		const data = {
			username: this.state.username,
			password: this.state.password
		};
		return axios.post(url, data).then(({ data }) => {
			console.log(data);
		});
	};

	getHelloWorld() {
		const url = 'http://localhost:5000/hello';
		axios.get(url).then(({ data }) => {
			this.setState({ hello: data });
		});
	}
	render() {
		const { hello } = this.state;

		return (
			<div className="containerz">
				<p style={{ textAlign: 'center', fontWeight: 'bolder' }}>Login first</p>
				<Form horizontal>
					<FormGroup controlId="formHorizontalEmail">
						<Col componentClass={ControlLabel} sm={2}>
							Email
						</Col>
						<Col sm={10}>
							<FormControl
								type="email"
								placeholder="Email"
								onChange={(e) => {
									this.setState({ username: e.target.value });
									console.log(e.target.value);
								}}
							/>
						</Col>
					</FormGroup>

					<FormGroup controlId="formHorizontalPassword">
						<Col componentClass={ControlLabel} sm={2}>
							Password
						</Col>
						<Col sm={10}>
							<FormControl
								type="password"
								placeholder="Password"
								onChange={(e) => {
									this.setState({ password: e.target.value });
								}}
							/>
						</Col>
					</FormGroup>

					<FormGroup>
						<Col smOffset={2} sm={10}>
							<Checkbox>Remember me</Checkbox>
						</Col>
					</FormGroup>

					<FormGroup>
						<Col smOffset={2} sm={10}>
							<Button type="submit" onClick={this.handleLogin}>
								Sign in
							</Button>
						</Col>
					</FormGroup>
				</Form>
			</div>
		);
	}
}

export default Login;
